import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const TicketForm = () => {
  const [tickets, setTickets] = useState([]);
  const [completedTickets, setCompletedTickets] = useState([]);
  const [description, setDescription] = useState("");
  const [categoryId, setCategoryId] = useState("");
  const [priorityId, setPriorityId] = useState("");
  const navigate = useNavigate();
  const token = localStorage.getItem("token");

  useEffect(() => {
    if (!token) {
      navigate("/login");
    }
  }, [token, navigate]);

  const categories = [
    { categoryId: 1, name: "Yazƒ±lƒ±m Hatasƒ±" },
    { categoryId: 2, name: "Donanƒ±m Sorunu" },
    { categoryId: 3, name: "Kullanƒ±cƒ± Eri≈üimi" },
    { categoryId: 4, name: "Aƒü Problemleri" },
  ];

  const priorities = [
    { priorityId: 1, name: "D√º≈ü√ºk" },
    { priorityId: 2, name: "Orta" },
    { priorityId: 3, name: "Y√ºksek" },
    { priorityId: 4, name: "Acil" },
  ];

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!token) {
      alert("Oturum s√ºresi dolmu≈ü. L√ºtfen yeniden giri≈ü yapƒ±n.");
      navigate("/login");
      return;
    }

    const ticketData = {
      description,
      categoryId: parseInt(categoryId),
      priorityId: parseInt(priorityId),
    };

    try {
      const response = await axios.post("http://localhost:5094/api/Ticket/createTicket", ticketData, {
        headers: {
          Authorization: `Bearer ${token}`,
        }        
      });

      if (!response.data || !response.data.id) {
        throw new Error("Talep olu≈üturulamadƒ±.");
      }

      alert("Talep ba≈üarƒ±yla g√∂nderildi!");

      setTickets([...tickets, { id: response.data.id, description }]);

      setDescription("");
      setCategoryId("");
      setPriorityId("");
    } catch (error) {
      console.error("Hata:", error.message);
      alert("Bir hata olu≈ütu. L√ºtfen tekrar deneyin.");
    }
  };

  const ticketDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5094/api/Ticket/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setTickets(tickets.filter((t) => t.id !== id));
      setCompletedTickets(completedTickets.filter((t) => t.id !== id));
    } catch (error) {
      console.error("Silme hatasƒ±:", error.message);
    }
  };

  const markCompleted = (id) => {
    const selectedTicket = tickets.find((t) => t.id === id);
    if (selectedTicket) {
      setCompletedTickets([...completedTickets, selectedTicket]);
      setTickets(tickets.filter((t) => t.id !== id));
    }
  };

  const logout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("username");
    navigate("/login");
  };

  return (
    <form onSubmit={handleSubmit} className="max-w-md mx-auto p-6 bg-white shadow rounded-xl space-y-4">
      <h2 className="text-xl font-bold text-center">Yeni Ticket</h2>

      <button onClick={logout} className="text-sm text-red-600 hover:underline">
        √áƒ±kƒ±≈ü Yap
      </button>

      <div>
        <label className="block text-sm font-medium">A√ßƒ±klama</label>
        <textarea
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
          className="mt-1 block w-full border p-2 rounded"
        />
      </div>

      <div>
        <label className="block text-sm font-medium">Kategori</label>
        <select
          value={categoryId}
          onChange={(e) => setCategoryId(e.target.value)}
          required
          className="mt-1 block w-full border p-2 rounded"
        >
          <option value="" disabled>üóÇÔ∏è Kategori se√ßin</option>
          {categories.map((cat) => (
            <option key={cat.categoryId} value={cat.categoryId}>
              {cat.name}
            </option>
          ))}
        </select>
      </div>

      <div>
        <label className="block text-sm font-medium">√ñncelik</label>
        <select
          value={priorityId}
          onChange={(e) => setPriorityId(e.target.value)}
          required
          className="mt-1 block w-full border p-2 rounded"
        >
          <option value="" disabled>‚ö° √ñncelik se√ßin</option>
          {priorities.map((pri) => (
            <option key={pri.priorityId} value={pri.priorityId}>
              {pri.name}
            </option>
          ))}
        </select>
      </div>

      <button
        type="submit"
        className="w-full bg-blue-600 text-white py-2 px-4 rounded hover:bg-blue-700"
      >
        G√∂nder
      </button>

      <div>
        <h3 className="font-semibold">Aktif Talepler</h3>
        <ul>
          {tickets.map((ticket) => (
            <li key={ticket.id} className="flex justify-between items-center">
              {ticket.description}
              <div>
                <button onClick={() => markCompleted(ticket.id)} className="text-green-600 mr-2">Tamamlandƒ±</button>
                <button onClick={() => ticketDelete(ticket.id)} className="text-red-600">Sil</button>
              </div>
            </li>
          ))}
        </ul>
      </div>

      <div>
        <h3 className="font-semibold">Tamamlanan Talepler</h3>
        <ul>
          {completedTickets.map((completedTicket) => (
            <li key={completedTicket.id} className="flex justify-between items-center">
              <del>{completedTicket.description}</del>
              <button onClick={() => ticketDelete(completedTicket.id)} className="text-red-600">Sil</button>
            </li>
          ))}
        </ul>
      </div>
    </form>
  );
};

export default TicketForm;
