import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./TicketPage.css";
import { jwtDecode } from "jwt-decode";

const API_URL = "http://localhost:5094/api/Ticket";

const TicketForm = () => {
  const [tickets, setTickets] = useState([]);
  const [description, setDescription] = useState("");
  const [categoryId, setCategoryId] = useState("");
  const [priorityId, setPriorityId] = useState("");
  const navigate = useNavigate();
  const token = localStorage.getItem("token");

  const fetchTickets = useCallback(async () => {
    try {
      const response = await axios.get(`${API_URL}/tickets`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTickets(response.data);
    } catch (error) {
      console.error("Ticketlar alınırken hata:", error);
    }
  }, [token]);

  useEffect(() => {
    if (!token) {
      navigate("/login");
    } else {
      try {
        const decoded = jwtDecode(token);
        if (!decoded || !decoded.userId) {
          navigate("/login");
        } else {
          fetchTickets();
        }
      } catch {
        navigate("/login");
      }
    }
  }, [token, navigate, fetchTickets]);

  const categories = [
    { categoryId: 1, name: "Yazılım Hatası" },
    { categoryId: 2, name: "Donanım Sorunu" },
    { categoryId: 3, name: "Kullanıcı Erişimi" },
    { categoryId: 4, name: "Ağ Problemleri" },
  ];

  const priorities = [
    { priorityId: 1, name: "Düşük" },
    { priorityId: 2, name: "Orta" },
    { priorityId: 3, name: "Yüksek" },
    { priorityId: 4, name: "Acil" },
  ];

  const handleSubmit = async (e) => {
    e.preventDefault();
    const ticketData = {
      description,
      categoryId: parseInt(categoryId),
      priorityId: parseInt(priorityId),
    };

    try {
      await axios.post(`${API_URL}/createTicket`, ticketData, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert("Talep başarıyla gönderildi!");
      setDescription("");
      setCategoryId("");
      setPriorityId("");
      fetchTickets();
    } catch (error) {
      console.error("Hata:", error.message);
      alert("Bir hata oluştu. Lütfen tekrar deneyin.");
    }
  };

  const markAsResolved = async (ticket) => {
    try {
      const updatedTicket = {
        ...ticket,
        isCompleted: true,
      };
      await axios.put(`${API_URL}/updateTicket/${ticket.ticketId}`, updatedTicket, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchTickets();
    } catch (error) {
      console.error("Tamamlama hatası:", error.message);
    }
  };

  const deleteTicket = async (id) => {
    try {
      await axios.delete(`${API_URL}/deleteTicket/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchTickets();
    } catch (error) {
      console.error("Silme hatası:", error.message);
    }
  };

  return (
    <div className="container">
      <div className="sidebar">
        <div className="logo">HD</div>
        <ul className="nav">
          <li><span className="icon">🎫</span> <span className="label">Tickets</span></li>
          <li><span className="icon">👥</span> <span className="label">Agents</span></li>
          <li><span className="icon">⚙️</span> <span className="label">Settings</span></li>
        </ul>
        <div className="profile">
          <img src="https://i.pravatar.cc/40" alt="user" />
          <span>User</span>
        </div>
      </div>

      <div className="main">
        <div className="form-box">
          <h2>Yeni Talep Oluştur</h2>
          <form onSubmit={handleSubmit}>
            <label>Açıklama</label>
            <textarea
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              placeholder="Talep açıklamasını yazınız..."
              rows="4"
              required
            />

            <label>Kategori</label>
            <select value={categoryId} onChange={(e) => setCategoryId(e.target.value)} required>
              <option value="">Kategori Seçin</option>
              {categories.map((cat) => (
                <option key={cat.categoryId} value={cat.categoryId}>{cat.name}</option>
              ))}
            </select>

            <label>Öncelik</label>
            <select value={priorityId} onChange={(e) => setPriorityId(e.target.value)} required>
              <option value="">Öncelik Seçin</option>
              {priorities.map((pri) => (
                <option key={pri.priorityId} value={pri.priorityId}>{pri.name}</option>
              ))}
            </select>

            <button type="submit">Gönder</button>
          </form>
        </div>

        <div className="ticket-list">
          <h3>Mevcut Talepler</h3>
          <ul>
            {tickets.map((ticket) => (
              <li key={ticket.ticketId}>
                <strong>{ticket.description}</strong> - {ticket.isCompleted ? "✔ Tamamlandı" : "⏳ Bekliyor"}
                <div>
                  {!ticket.isCompleted && (
                    <button onClick={() => markAsResolved(ticket)}>Tamamlandı</button>
                  )}
                  <button onClick={() => deleteTicket(ticket.ticketId)}>Sil</button>
                </div>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default TicketForm;
