import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./TicketPage.css";
import { jwtDecode } from "jwt-decode";

const API_URL = "http://localhost:5094/api/Ticket";

const TicketForm = () => {
  const [tickets, setTickets] = useState([]);
  const [description, setDescription] = useState("");
  const [categoryId, setCategoryId] = useState("");
  const [priorityId, setPriorityId] = useState("");
  const navigate = useNavigate();
  const token = localStorage.getItem("token");

  const fetchTickets = useCallback(async () => {
    try {
      const response = await axios.get(`${API_URL}/mytickets`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTickets(response.data);
    } catch (error) {
      console.error("Ticketlar alƒ±nƒ±rken hata:", error);
    }
  }, [token]);

  useEffect(() => {
  if (!token) {
    navigate("/login");
  } else {
    fetchTickets(); // ticketlarƒ± giri≈ü yapmƒ±≈ü kullanƒ±cƒ± i√ßin getir
  }
}, [token, navigate, fetchTickets]);



  const categories = [
    { categoryId: 1, name: "Yazƒ±lƒ±m Hatasƒ±" },
    { categoryId: 2, name: "Donanƒ±m Sorunu" },
    { categoryId: 3, name: "Kullanƒ±cƒ± Eri≈üimi" },
    { categoryId: 4, name: "Aƒü Problemleri" },
  ];

  const priorities = [
    { priorityId: 1, name: "D√º≈ü√ºk" },
    { priorityId: 2, name: "Orta" },
    { priorityId: 3, name: "Y√ºksek" },
    { priorityId: 4, name: "Acil" },
  ];

  const handleSubmit = async (e) => {
    e.preventDefault();

    const ticketData = {
      description,
      categoryId: parseInt(categoryId),
      priorityId: parseInt(priorityId),
    };

    try {
      const response = await axios.post(`${API_URL}/createTicket`, ticketData, {
        headers: { Authorization: `Bearer ${token}` },
      });

      alert("Talep ba≈üarƒ±yla g√∂nderildi!");
      setDescription("");
      setCategoryId("");
      setPriorityId("");
      fetchTickets();
    } catch (error) {
      console.error("Hata:", error.message);
      alert("Bir hata olu≈ütu. L√ºtfen tekrar deneyin.");
    }
  };

  const markAsResolved = async (id) => {
    try {
      await axios.put(`${API_URL}/${id}`, { isResolved: true }, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchTickets();
    } catch (error) {
      console.error("Tamamlama hatasƒ±:", error.message);
    }
  };

  const deleteTicket = async (id) => {
    try {
      await axios.delete(`${API_URL}/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchTickets();
    } catch (error) {
      console.error("Silme hatasƒ±:", error.message);
    }
  };

  return (
    <div className="container">
      <div className="sidebar">
        <div className="logo">HD</div>
        <ul className="nav">
          <li>
            <span className="icon">üé´</span> <span className="label">Tickets</span>
          </li>
          <li>
            <span className="icon">üë•</span> <span className="label">Agents</span>
          </li>
          <li>
            <span className="icon">‚öôÔ∏è</span> <span className="label">Settings</span>
          </li>
        </ul>
        <div className="profile">
          <img src="https://i.pravatar.cc/40" alt="user" />
          <span>User</span>
        </div>
      </div>

      <div className="main">
        <div className="form-box">
          <h2>Yeni Talep Olu≈ütur</h2>
          <form onSubmit={handleSubmit}>
            <label>A√ßƒ±klama</label>
            <textarea
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              placeholder="Talep a√ßƒ±klamasƒ±nƒ± yazƒ±nƒ±z..."
              rows="4"
              required
            />

            <label>Kategori</label>
            <select value={categoryId} onChange={(e) => setCategoryId(e.target.value)} required>
              <option value="">Kategori Se√ßin</option>
              {categories.map((cat) => (
                <option key={cat.categoryId} value={cat.categoryId}>{cat.name}</option>
              ))}
            </select>

            <label>√ñncelik</label>
            <select value={priorityId} onChange={(e) => setPriorityId(e.target.value)} required>
              <option value="">√ñncelik Se√ßin</option>
              {priorities.map((pri) => (
                <option key={pri.priorityId} value={pri.priorityId}>{pri.name}</option>
              ))}
            </select>

            <button type="submit">G√∂nder</button>
          </form>
        </div>

        <div className="ticket-list">
          <h3>Mevcut Talepler</h3>
          <ul>
            {tickets.map((ticket) => (
              <li key={ticket.id}>
                <strong>{ticket.description}</strong> - {ticket.isResolved ? "‚úî Tamamlandƒ±" : "‚è≥ Bekliyor"}
                <div>
                  {!ticket.isResolved && (
                    <button onClick={() => markAsResolved(ticket.id)}>Tamamlandƒ±</button>
                  )}
                  <button onClick={() => deleteTicket(ticket.id)}>Sil</button>
                </div>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default TicketForm;
