import React from "react";
import { useParams } from "react-router-dom";
import TicketDetailView from "./Components/TicketDetailView";

const TicketDetailWrapper = () => {
  const { ticketId } = useParams();

  // Burada kullanıcı rolüne göre kontrol yapılabilir
  const isSupportUser = true; // Örneğin localStorage'dan rol al

  return <TicketDetailView ticketId={ticketId} isSupportUser={isSupportUser} />;
};

export default TicketDetailWrapper;
