import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const TicketForm = () => {
  const [tickets, setTickets] = useState([]);
  const [completedTickets, setCompletedTickets] = useState([]);
  const [description, setDescription] = useState("");
  const [categoryId, setCategoryId] = useState("");
  const [priorityId, setPriorityId] = useState("");
  const navigate = useNavigate();
  const token = localStorage.getItem("token");

  useEffect(() => {
    if (!token) {
      navigate("/login");
    }
  }, [token, navigate]);

  const categories = [
    { categoryId: 1, name: "Yazılım Hatası" },
    { categoryId: 2, name: "Donanım Sorunu" },
    { categoryId: 3, name: "Kullanıcı Erişimi" },
    { categoryId: 4, name: "Ağ Problemleri" },
  ];

  const priorities = [
    { priorityId: 1, name: "Düşük" },
    { priorityId: 2, name: "Orta" },
    { priorityId: 3, name: "Yüksek" },
    { priorityId: 4, name: "Acil" },
  ];

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!token) {
      alert("Oturum süresi dolmuş. Lütfen yeniden giriş yapın.");
      navigate("/login");
      return;
    }

    const ticketData = {
      description,
      categoryId: parseInt(categoryId),
      priorityId: parseInt(priorityId),
    };

    try {
      const response = await axios.post("http://localhost:5094/api/Ticket/createTicket", ticketData, {
        headers: {
          Authorization: `Bearer ${token}`,
        }        
      });

      if (!response.data || !response.data.id) {
        throw new Error("Talep oluşturulamadı.");
      }

      alert("Talep başarıyla gönderildi!");

      setTickets([...tickets, { id: response.data.id, description }]);

      setDescription("");
      setCategoryId("");
      setPriorityId("");
    } catch (error) {
      console.error("Hata:", error.message);
      alert("Bir hata oluştu. Lütfen tekrar deneyin.");
    }
  };

  const ticketDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5094/api/Ticket/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setTickets(tickets.filter((t) => t.id !== id));
      setCompletedTickets(completedTickets.filter((t) => t.id !== id));
    } catch (error) {
      console.error("Silme hatası:", error.message);
    }
  };

  const markCompleted = (id) => {
    const selectedTicket = tickets.find((t) => t.id === id);
    if (selectedTicket) {
      setCompletedTickets([...completedTickets, selectedTicket]);
      setTickets(tickets.filter((t) => t.id !== id));
    }
  };

  const logout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("username");
    navigate("/login");
  };

  return (
    <div className="flex h-screen bg-gray-100 font-sans">
      {/* Sidebar */}
      <div className="w-20 md:w-64 bg-gray-800 text-white flex flex-col justify-between">
        <div>
          <div className="text-2xl font-bold text-center py-6">HD</div>
          <nav className="space-y-2 text-sm px-4">
            <div className="flex items-center space-x-2 py-2 hover:bg-gray-700 cursor-pointer">
              <span className="material-icons">confirmation_number</span>
              <span className="hidden md:inline">Tickets</span>
              <span className="ml-auto bg-red-500 text-xs rounded-full px-2 py-0.5">99+</span>
            </div>
            <div className="flex items-center space-x-2 py-2 hover:bg-gray-700 cursor-pointer">
              <span className="material-icons">people</span>
              <span className="hidden md:inline">Agents</span>
            </div>
            <div className="flex items-center space-x-2 py-2 hover:bg-gray-700 cursor-pointer">
              <span className="material-icons">settings</span>
              <span className="hidden md:inline">Settings</span>
            </div>
          </nav>
        </div>
        <div className="p-4">
          <div className="flex items-center space-x-2">
            <img
              src="https://i.pravatar.cc/40"
              alt="User"
              className="rounded-full w-8 h-8"
            />
            <span className="hidden md:inline">User</span>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="flex-1 p-8 overflow-y-auto">
        <div className="max-w-2xl mx-auto bg-white p-8 rounded shadow">
          <h2 className="text-2xl font-semibold mb-6 text-center">New Ticket</h2>

          <form className="space-y-4">
            <div>
              <label className="block font-medium">Subject</label>
              <input
                type="text"
                className="w-full p-2 border border-gray-300 rounded"
                placeholder="Enter subject"
              />
            </div>

            <div className="flex space-x-2">
              <div className="flex-1">
                <label className="block font-medium">Requester</label>
                <input
                  type="text"
                  className="w-full p-2 border border-gray-300 rounded"
                  placeholder="Full name"
                />
              </div>
              <div className="flex-1">
                <label className="block font-medium invisible">Email</label>
                <input
                  type="email"
                  className="w-full p-2 border border-gray-300 rounded"
                  placeholder="email@example.com"
                />
              </div>
            </div>

            <div>
              <label className="block font-medium">Team</label>
              <div className="flex items-center justify-between border p-2 rounded">
                <span>Support Heroes</span>
                <button className="text-blue-600 text-sm">Change</button>
              </div>
            </div>

            <div>
              <label className="block font-medium">Agent</label>
              <div className="flex items-center justify-between border p-2 rounded">
                <span className="flex items-center space-x-2">
                  <span className="material-icons">person_outline</span>
                  <span>Unassigned</span>
                </span>
                <button className="text-blue-600 text-sm">Assign</button>
              </div>
            </div>

            <div>
              <label className="block font-medium">Message</label>
              <textarea
                className="w-full p-2 border border-gray-300 rounded"
                placeholder="Type a message..."
                rows="4"
              />
            </div>

            <button
              type="submit"
              className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
            >
              Submit
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default TicketForm;
