import React, { useEffect, useState } from 'react';
import axios from 'axios';
import TicketDetailView from '../Components/TicketDetailView';
import './SupportDashboard.css';
import { useNavigate } from 'react-router-dom';

const SupportDashboard = () => {
  const [tickets, setTickets] = useState([]);
  const navigate = useNavigate();
  const [selectedTicketId, setSelectedTicketId] = useState(null);

  useEffect(() => {
    const fetchTickets = async () => {
      try {
        const token = localStorage.getItem('token');

        if (!token) {
          console.error('Token bulunamadÄ±. GiriÅŸ yapmanÄ±z gerekiyor.');
          return;
        }

        const res = await axios.get('http://localhost:5094/api/ticket/getAllTicketsByRole', {
          headers: { Authorization: `Bearer ${token}` }
        });

        setTickets(res.data);
      } catch (err) {
        console.error('Ticketlar alÄ±namadÄ±:', err);
        alert('Ticketlar yÃ¼klenirken bir hata oluÅŸtu. LÃ¼tfen tekrar deneyin.');
      }
    };

    fetchTickets();
  }, []);

  return (
    <div className="support-container">
      <div className="sidebar">
        <div className="logo">HelpDesk</div>
        <ul className="nav">
          <li onClick={() => navigate('/ticket-list')} style={{ cursor: 'pointer' }}>
            <span className="icon">ðŸŽ«</span> <span className="label">Tickets</span>
          </li>
        </ul>

        <div className="profile">
          <img src="https://i.pravatar.cc/40" alt="support" />
          <span>Destek KullanÄ±cÄ±sÄ±</span>
        </div>
      </div>

      <div className="main-content">
        {!selectedTicketId ? (
          <>
            <h2>Gelen Ticketlar</h2>
            <ul className="ticket-list">
              {tickets.map(ticket => (
                <li key={ticket.id} onClick={() => setSelectedTicketId(ticket.id)}>
                  <strong>{ticket.description}</strong><br />
                  Kategori: {ticket.category} - Ã–ncelik: {ticket.priority}
                </li>
              ))}
            </ul>
          </>
        ) : (
          <div className="ticket-detail-container">
            <div className="message-area">
              <h3>YanÄ±t Ver</h3>
              <textarea placeholder="YanÄ±tÄ±nÄ±zÄ± buraya yazÄ±n..." rows="4" />
              <button className="reply-button">GÃ¶nder</button>
            </div>
            <div className="detail-area">
              <TicketDetailView ticketId={selectedTicketId} isSupportUser={true} />
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default SupportDashboard;
