import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Select, SelectItem } from '@/components/ui/select';

const CreateTicket = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [categoryId, setCategoryId] = useState('');
  const [priorityId, setPriorityId] = useState('');
  const [categories, setCategories] = useState([]);
  const [priorities, setPriorities] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [catRes, priRes] = await Promise.all([
          axios.get('/api/categories'),
          axios.get('/api/priorities')
        ]);
        setCategories(catRes.data);
        setPriorities(priRes.data);
      } catch (error) {
        console.error('Veriler yüklenemedi:', error);
      }
    };
    fetchData();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/tickets', {
        title,
        description,
        categoryId,
        priorityId
      });
      alert('Ticket başarıyla oluşturuldu.');
      setTitle('');
      setDescription('');
      setCategoryId('');
      setPriorityId('');
    } catch (error) {
      console.error('Ticket oluşturulamadı:', error);
      alert('Ticket oluşturulamadı.');
    }
  };

  return (
    <div className="max-w-2xl mx-auto p-6 bg-white rounded-xl shadow-md">
      <h2 className="text-xl font-semibold mb-4">Yeni Ticket Oluştur</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <Input
          placeholder="Başlık"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <Textarea
          placeholder="Açıklama"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        />
        <Select
          value={categoryId}
          onValueChange={setCategoryId}
          required
        >
          <SelectItem value="" disabled>Kategori Seçin</SelectItem>
          {categories.map((cat) => (
            <SelectItem key={cat.id} value={cat.id.toString()}>
              {cat.name}
            </SelectItem>
          ))}
        </Select>
        <Select
          value={priorityId}
          onValueChange={setPriorityId}
          required
        >
          <SelectItem value="" disabled>Öncelik Seçin</SelectItem>
          {priorities.map((pri) => (
            <SelectItem key={pri.id} value={pri.id.toString()}>
              {pri.name}
            </SelectItem>
          ))}
        </Select>
        <Button type="submit" className="w-full">
          Ticket Oluştur
        </Button>
      </form>
    </div>
  );
};

export default CreateTicket;
