import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import 'ag-grid-community/styles/ag-grid.css';
import 'ag-grid-community/styles/ag-theme-alpine.css';

const TicketResponse = () => {
  const { ticketId } = useParams();

  const [ticket, setTicket] = useState(null);
  const [responses, setResponses] = useState([]);
  const [responseText, setResponseText] = useState('');
  const [loadingResponse, setLoadingResponse] = useState(false);
  const [isSupportUser, setIsSupportUser] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');
        const headers = { Authorization: `Bearer ${token}` };

        const ticketRes = await axios.get(`http://localhost:5094/api/ticket/${ticketId}`, { headers });
        setTicket(ticketRes.data);

        const responseRes = await axios.get(`http://localhost:5094/api/ticket/${ticketId}/responses`, { headers });
        setResponses(responseRes.data);



        const userRole = localStorage.getItem('role');
        setIsSupportUser(userRole?.toLowerCase() === 'support');

        console.log("Kullanƒ±cƒ± Rol√º:", userRole);
        console.log("Ticket Durumu:", ticketRes.data.status);

      } catch (error) {
        console.error('Veriler alƒ±namadƒ±:', error);
      }
    };

    fetchData();
  }, [ticketId]);

  const fetchResponses = async () => {
    try {
      const token = localStorage.getItem('token');
      const headers = { Authorization: `Bearer ${token}` };

      const responseRes = await axios.get(`http://localhost:5094/api/ticket/${ticketId}/responses`, { headers });
      setResponses(responseRes.data);
    } catch (error) {
      console.error('Yanƒ±tlar alƒ±namadƒ±:', error);
    }
  };

  const handleSubmitResponse = async () => {
    if (!responseText.trim()) return;

    try {
      setLoadingResponse(true);
      const token = localStorage.getItem('token');
      const headers = { Authorization: `Bearer ${token}` };
      const currentUserId = localStorage.getItem('userId'); // LocalStorage‚Äôdan alƒ±nƒ±yor

      const res = await axios.post(`http://localhost:5094/api/ticket/${ticketId}/responses`, {
        response: responseText,
        ticketId: parseInt(ticketId),
        userId: parseInt(currentUserId)
      }, { headers });

      setResponses(prev => [...prev, res.data]);
      setResponseText('');
    } catch (error) {
      console.error('Yanƒ±t g√∂nderilemedi:', error);
    } finally {
      setLoadingResponse(false);
    }
  };


  if (!ticket) return <div>Y√ºkleniyor...</div>;

  return (
    <div className="container">
      <div className="sidebar">
        <div className="logo">HelpDesk</div>
        <ul className="nav">
          <li><span className="icon">üé´</span> <span className="label">Tickets</span></li>
          <li><span className="icon">üë•</span> <span className="label">Agents</span></li>
          <li><span className="icon">‚öôÔ∏è</span> <span className="label">Settings</span></li>
        </ul>
        <div className="profile">
          <img src="https://i.pravatar.cc/40" alt="user" />
        </div>
      </div>

      <div className="ticket-detail-area">
        <h2>Ticket Detaylarƒ±</h2>
        <p><strong>A√ßƒ±klama:</strong> {ticket.description}</p>
        <p><strong>Kategori:</strong> {ticket.category}</p>
        <p><strong>√ñncelik:</strong> {ticket.priority}</p>
        <p><strong>Olu≈üturulma Tarihi:</strong> {ticket.createdAt ? new Date(ticket.createdAt).toLocaleString() : "Tarih yok"}</p>
        <p><strong>Durum:</strong> {ticket.status}</p>

        <h3>Yanƒ±tlar</h3>
        <ul className="response-list">
          {responses.map((res, index) => (
            <li key={index}>
              <strong>Kullanƒ±cƒ± {res.userId}:</strong> {res.response}<br />
              <small>{new Date(res.dateTime).toLocaleString()}</small>
            </li>
          ))}
        </ul>

        {(isSupportUser || ticket?.status?.toLowerCase() === 'a√ßƒ±k') && (
          <div className="response-form">
            <h3>Yanƒ±t Ver</h3>
            <textarea
              rows="10"
              value={responseText}
              onChange={(e) => setResponseText(e.target.value)}
              placeholder="Yanƒ±tƒ±nƒ±zƒ± yazƒ±nƒ±z..."
              disabled={loadingResponse}
            />
            <button onClick={handleSubmitResponse} disabled={loadingResponse || !responseText.trim()}>
              {loadingResponse ? 'G√∂nderiliyor...' : 'Yanƒ±t G√∂nder'}
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default TicketResponse;
