import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import MessageBubble from "../Components/MessageBubble";
import { useAuth } from "../context/AuthContext";

const TicketDetailView = () => {
  const { ticketId } = useParams();
  const navigate = useNavigate();
  const [ticket, setTicket] = useState(null);
  const [responseText, setResponseText] = useState('');
  const [responses, setResponses] = useState([]);
  const [loadingResponse, setLoadingResponse] = useState(false);
  const [isSupportUser, setIsSupportUser] = useState(false);
  const { user } = useAuth();

  useEffect(() => {
    const fetchTicketData = async () => {
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/login');
        return;
      }

      try {
        const decoded = JSON.parse(atob(token.split('.')[1]));
        setIsSupportUser(decoded.role === 'Support');

        const ticketRes = await axios.get(`http://localhost:5094/api/ticket/${ticketId}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setTicket(ticketRes.data);

        const responseRes = await axios.get(`http://localhost:5094/api/ticket/${ticketId}/responses`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setResponses(responseRes.data);
      } catch (err) {
        console.error("Ticket detayları alınamadı:", err);
      }
    };

    fetchTicketData();
  }, [ticketId, navigate]);

  const handleSubmitResponse = async () => {
    const token = localStorage.getItem('token');
    if (!token) return;

    try {
      setLoadingResponse(true);
      await axios.post(
        `http://localhost:5094/api/ticket/${ticketId}/responses`,
        JSON.stringify({ response: responseText }),
        {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );

      setResponseText('');

      const responseRes = await axios.get(`http://localhost:5094/api/ticket/${ticketId}/responses`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setResponses(responseRes.data);
    } catch (err) {
      console.error("Yanıt gönderilemedi:", err);
    } finally {
      setLoadingResponse(false);
    }
  };

  if (!ticket) return <div className="p-4">Yükleniyor...</div>;

  return (
    <div className="flex min-h-screen bg-gray-50">
      <aside className="w-64 bg-white border-r p-6 flex flex-col justify-between">
        <div>
          <h1 className="text-xl font-bold mb-6">HelpDesk</h1>
          <ul className="space-y-4">
            <li className="flex items-center gap-2 text-gray-700">
              <span>🎫</span> <span>Tickets</span>
            </li>
            <li className="flex items-center gap-2 text-gray-700">
              <span>👥</span> <span>Agents</span>
            </li>
            <li className="flex items-center gap-2 text-gray-700">
              <span>⚙️</span> <span>Settings</span>
            </li>
          </ul>
        </div>
        <div className="flex items-center gap-2 mt-6">
          <img src="https://i.pravatar.cc/40" alt="user" className="rounded-full" />
          <span className="text-gray-700">User</span>
        </div>
      </aside>

      <main className="flex-1 p-8 overflow-y-auto">
        <h2 className="text-2xl font-semibold mb-4">Ticket Detayları</h2>
        <div className="space-y-2 mb-6">
          <p><strong>Açıklama:</strong> {ticket.description}</p>
          <p><strong>Kategori:</strong> {ticket.category}</p>
          <p><strong>Öncelik:</strong> {ticket.priority}</p>
          <p><strong>Oluşturulma Tarihi:</strong> {ticket.createdAt ? new Date(ticket.createdAt).toLocaleString() : "Tarih yok"}</p>
          <p><strong>Durum:</strong> {ticket.status}</p>
        </div>

        <h3 className="text-xl font-semibold mb-2">Yanıtlar</h3>
        {responses.length === 0 ? (
          <p className="text-gray-500">Henüz yanıt yok.</p>
        ) : (
          <div className="flex flex-col gap-2 my-4 max-h-[300px] overflow-y-auto bg-white p-4 rounded shadow-inner">
            {responses.map((res, index) => (
              <MessageBubble key={index} message={res} />
            ))}
          </div>
        )}

        {(isSupportUser || ticket.status === 'Yanıtlandı') && (
          <div className="mt-6">
            <h3 className="text-xl font-semibold mb-2">Yanıt Ver</h3>
            <textarea
              rows="3"
              value={responseText}
              onChange={(e) => setResponseText(e.target.value)}
              placeholder="Yanıtınızı yazınız..."
              disabled={loadingResponse}
              className="border rounded-lg p-3 w-full resize-none focus:outline-none focus:ring-2 focus:ring-blue-400"
            />
            <button
              onClick={handleSubmitResponse}
              disabled={loadingResponse || !responseText.trim()}
              className="mt-2 bg-blue-500 text-white px-6 py-2 rounded hover:bg-blue-600 disabled:opacity-50 transition"
            >
              {loadingResponse ? 'Gönderiliyor...' : 'Yanıt Gönder'}
            </button>
          </div>
        )}
      </main>
    </div>
  );
};

export default TicketDetailView;
