import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./TicketPage.css";

const TicketForm = () => {
  const [tickets, setTickets] = useState([]);
  const [description, setDescription] = useState("");
  const [categoryId, setCategoryId] = useState("");
  const [priorityId, setPriorityId] = useState("");
  const navigate = useNavigate();
  const token = localStorage.getItem("token");

  useEffect(() => {
    if (!token) {
      navigate("/login");
    }
  }, [token, navigate]);

  const categories = [
    { categoryId: 1, name: "Yazılım Hatası" },
    { categoryId: 2, name: "Donanım Sorunu" },
    { categoryId: 3, name: "Kullanıcı Erişimi" },
    { categoryId: 4, name: "Ağ Problemleri" },
  ];

  const priorities = [
    { priorityId: 1, name: "Düşük" },
    { priorityId: 2, name: "Orta" },
    { priorityId: 3, name: "Yüksek" },
    { priorityId: 4, name: "Acil" },
  ];

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!token) {
      alert("Oturum süresi dolmuş. Lütfen yeniden giriş yapın.");
      navigate("/login");
      return;
    }

    const ticketData = {
      description,
      categoryId: parseInt(categoryId),
      priorityId: parseInt(priorityId),
    };

    try {
      const response = await axios.post("http://localhost:5094/api/Ticket/createTicket", ticketData, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json"
        }
      });

      alert("Talep başarıyla gönderildi!");
      setTickets([...tickets, response.data]);
      setDescription("");
      setCategoryId("");
      setPriorityId("");
    } catch (error) {
      console.error("Hata:", error.message);
      alert("Bir hata oluştu. Lütfen tekrar deneyin.");
    }
  };

  return (
    <div className="container">
      <div className="sidebar">
        <div className="logo">HD</div>
        <ul className="nav">
          <li>
            <span className="icon">🎫</span> <span className="label">Tickets</span>
          </li>
          <li>
            <span className="icon">👥</span> <span className="label">Agents</span>
          </li>
          <li>
            <span className="icon">⚙️</span> <span className="label">Settings</span>
          </li>
        </ul>
        <div className="profile">
          <img src="https://i.pravatar.cc/40" alt="user" />
          <span>User</span>
        </div>
      </div>

      <div className="main">
        <div className="form-box">
          <h2>Yeni Talep Oluştur</h2>
          <form onSubmit={handleSubmit}>
            <label>Açıklama</label>
            <textarea
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              placeholder="Talep açıklamasını yazınız..."
              rows="4"
              required
            />

            <label>Kategori</label>
            <select value={categoryId} onChange={(e) => setCategoryId(e.target.value)} required>
              <option value="">Kategori Seçin</option>
              {categories.map((cat) => (
                <option key={cat.categoryId} value={cat.categoryId}>{cat.name}</option>
              ))}
            </select>

            <label>Öncelik</label>
            <select value={priorityId} onChange={(e) => setPriorityId(e.target.value)} required>
              <option value="">Öncelik Seçin</option>
              {priorities.map((pri) => (
                <option key={pri.priorityId} value={pri.priorityId}>{pri.name}</option>
              ))}
            </select>

            <button type="submit">Gönder</button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default TicketForm;
