import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './TicketDetailView.css'; 

const TicketDetailView = ({ ticketId, isSupportUser }) => {
  const [ticket, setTicket] = useState(null);
  const [responseText, setResponseText] = useState('');
  const [responses, setResponses] = useState([]);

  useEffect(() => {
    const fetchTicket = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get(`http://localhost:5094/api/ticket/${ticketId}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setTicket(res.data);

        const responseRes = await axios.get(`http://localhost:5094/api/ticket/${ticketId}/responses`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setResponses(responseRes.data);
      } catch (err) {
        console.error("Ticket detayları alınamadı:", err);
      }
    };

    fetchTicket();
  }, [ticketId]);

  const handleSubmitResponse = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios.post(`http://localhost:5094/api/ticket/${ticketId}/responses`, 
        { message: responseText },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setResponseText('');
      // Yanıtlar yeniden çekilsin
      const responseRes = await axios.get(`http://localhost:5094/api/ticket/${ticketId}/responses`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setResponses(responseRes.data);
    } catch (err) {
      console.error("Yanıt gönderilemedi:", err);
    }
  };

  if (!ticket) return <div>Yükleniyor...</div>;

  return (
    <div className="ticket-detail">
      <h2>Ticket Detayları</h2>
        <p><strong>Açıklama:</strong> {ticket.description}</p>
        <p><strong>Kategori:</strong> {ticket.category?.name}</p>
        <p><strong>Öncelik:</strong> {ticket.priority?.name}</p>
        <p><strong>Durum:</strong> {ticket.isCompleted ? "Tamamlandı" : "Beklemede"}</p>
        <p><strong>Oluşturulma Tarihi:</strong> {new Date(ticket.dateTime).toLocaleString()}</p>


      <h3>Yanıtlar</h3>
      {responses.length === 0 ? (
        <p>Henüz yanıt yok.</p>
      ) : (
        <ul>
          {responses.map((res, index) => (
            <li key={index}>
              <strong>{res.responderName}:</strong> {res.message} <br />
              <small>{new Date(res.createdAt).toLocaleString()}</small>
            </li>
          ))}
        </ul>
      )}

      {isSupportUser && (
        <div className="response-form">
          <h3>Yanıt Ver</h3>
          <textarea
            rows="4"
            value={responseText}
            onChange={(e) => setResponseText(e.target.value)}
            placeholder="Yanıtınızı yazınız..."
          />
          <button onClick={handleSubmitResponse}>Yanıt Gönder</button>
        </div>
      )}
    </div>
  );
};

export default TicketDetailView;
