import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { AgGridReact } from "ag-grid-react";
import "ag-grid-community/styles/ag-grid.css";
import "ag-grid-community/styles/ag-theme-alpine.css";
import "./TicketPage.css";

const API_URL = "http://localhost:5094/api/Ticket";

const TicketForm = () => {
  const [tickets, setTickets] = useState([]);
  const [description, setDescription] = useState("");
  const [categoryId, setCategoryId] = useState("");
  const [priorityId, setPriorityId] = useState("");
  const [categories, setCategories] = useState([]);
  const [priorities, setPriorities] = useState([]);
  const [loading, setLoading] = useState(false);

  const navigate = useNavigate();
  const token = localStorage.getItem("token");

  // Talepleri getir
  const fetchTickets = useCallback(async () => {
    try {
      const response = await axios.get(`${API_URL}/mytickets`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTickets(response.data);
    } catch (error) {
      console.error("Ticketlar alƒ±nƒ±rken hata:", error);
    }
  }, [token]);

  // Kategori ve √∂ncelikleri getir
  const fetchMeta = useCallback(async () => {
    try {
      const [catRes, priRes] = await Promise.all([
        axios.get("http://localhost:5094/api/Category", {
          headers: { Authorization: `Bearer ${token}` },
        }),
        axios.get("http://localhost:5094/api/Priority", {
          headers: { Authorization: `Bearer ${token}` },
        }),
      ]);
      setCategories(catRes.data);
      setPriorities(priRes.data);
    } catch (err) {
      console.error("Kategori/√∂ncelik verileri alƒ±namadƒ±:", err);
    }
  }, [token]);

  useEffect(() => {
    if (!token) {
      navigate("/login");
    } else {
      fetchTickets();
      fetchMeta();
    }
  }, [token, navigate, fetchTickets, fetchMeta]);

  // Talep g√∂nderme
  const handleSubmit = async (e) => {
    e.preventDefault();

    const ticketData = {
      description,
      categoryId: parseInt(categoryId),
      priorityId: parseInt(priorityId),
    };

    try {
      await axios.post(`${API_URL}/createTicket`, ticketData, {
        headers: { Authorization: `Bearer ${token}` },
      });

      alert("Talep ba≈üarƒ±yla g√∂nderildi!");
      setDescription("");
      setCategoryId("");
      setPriorityId("");
      fetchTickets();
    } catch (error) {
      console.error("Hata:", error.message);
      alert("Bir hata olu≈ütu. L√ºtfen tekrar deneyin.");
    }
  };

  const markAsResolved = async (id) => {
    try {
      setLoading(true);
      await axios.put(
        `${API_URL}/${id}`,
        { isResolved: true },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      await fetchTickets();
    } catch (error) {
      console.error("Tamamlama hatasƒ±:", error.message);
    } finally {
      setLoading(false);
    }
  };

  const deleteTicket = async (id) => {
    try {
      setLoading(true);
      await axios.delete(`${API_URL}/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      await fetchTickets();
    } catch (error) {
      console.error("Silme hatasƒ±:", error.message);
    } finally {
      setLoading(false);
    }
  };

  // AG Grid kolonlarƒ±
  const columnDefs = [
    { headerName: "A√ßƒ±klama", field: "description", flex: 2 },
    {
      headerName: "Kategori",
      field: "categoryId",
      flex: 1,
      valueGetter: (params) =>
        categories.find((c) => c.categoryId === params.data.categoryId)?.name || "Bilinmiyor",
    },
    {
      headerName: "√ñncelik",
      field: "priorityId",
      flex: 1,
      valueGetter: (params) =>
        priorities.find((p) => p.priorityId === params.data.priorityId)?.name || "Bilinmiyor",
    },
    {
      headerName: "Durum",
      field: "isResolved",
      flex: 1,
      cellRenderer: (params) =>
        params.value ? "‚úî Tamamlandƒ±" : "‚è≥ Bekliyor",
    },
    {
      headerName: "ƒ∞≈ülemler",
      field: "actions",
      flex: 2,
      cellRendererFramework: (params) => (
        <div className="actions-cell">
          {!params.data.isResolved && (
            <button disabled={loading} onClick={() => markAsResolved(params.data.id)}>
              Tamamlandƒ±
            </button>
          )}
          <button disabled={loading} onClick={() => deleteTicket(params.data.id)}>
            Sil
          </button>
        </div>
      ),
    },
  ];

  return (
    <div className="container">
      <div className="sidebar">
        <div className="logo">HD</div>
        <ul className="nav">
          <li><span className="icon">üé´</span> <span className="label">Tickets</span></li>
          <li><span className="icon">üë•</span> <span className="label">Agents</span></li>
          <li><span className="icon">‚öôÔ∏è</span> <span className="label">Settings</span></li>
        </ul>
        <div className="profile">
          <img src="https://i.pravatar.cc/40" alt="user" />
          <span>User</span>
        </div>
      </div>

      <div className="main-grid">
        {/* Form */}
        <div className="form-box">
          <h2>Yeni Talep Olu≈ütur</h2>
          <form onSubmit={handleSubmit}>
            <label>A√ßƒ±klama</label>
            <textarea
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              placeholder="Talep a√ßƒ±klamasƒ±nƒ± yazƒ±nƒ±z..."
              rows="4"
              minLength={10}
              maxLength={500}
              required
            />

            <label>Kategori</label>
            <select value={categoryId} onChange={(e) => setCategoryId(e.target.value)} required>
              <option value="">Kategori Se√ßin</option>
              {categories.map((cat) => (
                <option key={cat.categoryId} value={cat.categoryId}>{cat.name}</option>
              ))}
            </select>

            <label>√ñncelik</label>
            <select value={priorityId} onChange={(e) => setPriorityId(e.target.value)} required>
              <option value="">√ñncelik Se√ßin</option>
              {priorities.map((pri) => (
                <option key={pri.priorityId} value={pri.priorityId}>{pri.name}</option>
              ))}
            </select>

            <button type="submit">G√∂nder</button>
          </form>
        </div>

        {/* AG Grid Liste */}
        <div className="ticket-list-box">
          <h2>Mevcut Talepler</h2>
          {tickets.length === 0 ? (
            <p>Hen√ºz bir talep olu≈üturulmamƒ±≈ü.</p>
          ) : (
            <div className="ag-theme-alpine" style={{ height: 400, width: "100%" }}>
              <AgGridReact
                rowData={tickets}
                columnDefs={columnDefs}
                pagination={true}
                paginationPageSize={10}
                domLayout="autoHeight"
              />
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default TicketForm;
