import React, { useEffect, useState, useCallback } from "react";
import { AgGridReact } from "ag-grid-react";
import "ag-grid-community/styles/ag-grid.css";
import "ag-grid-community/styles/ag-theme-alpine.css";
import { ClientSideRowModelModule } from '@ag-grid-community/client-side-row-model';
import { ModuleRegistry } from '@ag-grid-community/core';

const ListTickets = () => {
    const [rowData, setRowData] = useState([]);
    const navigation = useNavigate();
    const token = localStorage.getItem("token");
    const role = localStorage.getItem("role");

    const fetchTickets = useCallback(() => {
        fetch("http://localhost:5094/api/Ticket/getAllTickets", {
            method: "GET",
            headers: {
                Authorization: `Bearer ${token}`,
            },
        })
            .then((response) => response.json())
            .then((data) => {
                setRowData(data);
            })
            .catch((error) => {
                console.error("Error fetching tickets:", error);
            }, [token]);
        });

        useEffect(() => {
            if (!token) {
                navigate("/login");
            } else if (role !== "Admin") {
                alert("Bu sayfaya erişim izniniz yok.");
                navigate("/tickets"); 
            } else {
                fetchTickets();
            }
        }, [token, role, fetchTickets, navigate]);

        const columnDefs = [
            { field: "ticketId", headerName: "Ticket ID" },
            { field: "description", headerName: "Açıklama" },
            { field: "categoryId", headerName: "Kategori ID" },
            { field: "priorityId", headerName: "Öncelik ID" },
            { field: "status", headerName: "Durum" },
        ];

        return (
            <div className="ag-theme-alpine" style={{ height: 600, width: "100%" }}>
                <AgGridReact
                    rowData={rowData}
                    columnDefs={columnDefs}
                    pagination={true}
                    paginationPageSize={20}
                />
            </div>
        );
    };

export default ListTickets;
