import React, { useState, useEffect } from "react";
import axios from "axios";

const CreateTicket = () => {
  const [description, setDescription] = useState("");
  const [categoryId, setCategoryId] = useState("");
  const [priorityId, setPriorityId] = useState("");
  const [categories, setCategories] = useState([]);
  const [priorities, setPriorities] = useState([]);
  const [userId, setUserId] = useState(null); // Kullanıcıyı token'dan alacağız

  const token = localStorage.getItem("token");

  useEffect(() => {
    const fetchInitialData = async () => {
      try {
        // Kullanıcıyı token'dan al (örnek payload decode işlemi varsa buraya eklenir)
        const userResponse = await axios.get("http://localhost:5094/api/Auth/Me", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUserId(userResponse.data.userId);

        // Kategoriler ve öncelikler
        const [catRes, priRes] = await Promise.all([
          axios.get("http://localhost:5094/api/Categories", {
            headers: { Authorization: `Bearer ${token}` },
          }),
          axios.get("http://localhost:5094/api/Priorities", {
            headers: { Authorization: `Bearer ${token}` },
          }),
        ]);

        setCategories(catRes.data);
        setPriorities(priRes.data);
      } catch (error) {
        console.error("Veri alınırken hata:", error);
      }
    };

    fetchInitialData();
  }, [token]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(
        "http://localhost:5094/api/Tickets/createTicket",
        {
          description,
          categoryId: parseInt(categoryId),
          priorityId: parseInt(priorityId),
          userId: parseInt(userId),
          isCompleted: false,
        },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      alert("Ticket başarıyla oluşturuldu.");
      setDescription("");
      setCategoryId("");
      setPriorityId("");
    } catch (error) {
      alert("Ticket oluşturulurken hata: " + error.message);
    }
  };

  return (
    <div>
      <h2>Yeni Ticket Oluştur</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Açıklama:</label><br />
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          />
        </div>

        <div>
          <label>Kategori:</label><br />
          <select
            value={categoryId}
            onChange={(e) => setCategoryId(e.target.value)}
            required
          >
            <option value="User">1</option>
            <option value="Admin">2</option>
          </select>
        </div>

        <div>
          <label>Öncelik:</label><br />
          <select
            value={priorityId}
            onChange={(e) => setPriorityId(e.target.value)}
            required
          >
            <option value="User">1</option>
            <option value="Admin">2</option>
          </select>
        </div>

        <button type="submit">Oluştur</button>
      </form>
    </div>
  );
};

export default CreateTicket;
