import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./TicketPage.css"
const TicketForm = () => {
  const [tickets, setTickets] = useState([]);
  const [completedTickets, setCompletedTickets] = useState([]);
  const [description, setDescription] = useState("");
  const [categoryId, setCategoryId] = useState("");
  const [priorityId, setPriorityId] = useState("");
  const navigate = useNavigate();
  const token = localStorage.getItem("token");

  useEffect(() => {
    if (!token) {
      navigate("/login");
    }
  }, [token, navigate]);

  const categories = [
    { categoryId: 1, name: "Yazƒ±lƒ±m Hatasƒ±" },
    { categoryId: 2, name: "Donanƒ±m Sorunu" },
    { categoryId: 3, name: "Kullanƒ±cƒ± Eri≈üimi" },
    { categoryId: 4, name: "Aƒü Problemleri" },
  ];

  const priorities = [
    { priorityId: 1, name: "D√º≈ü√ºk" },
    { priorityId: 2, name: "Orta" },
    { priorityId: 3, name: "Y√ºksek" },
    { priorityId: 4, name: "Acil" },
  ];

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!token) {
      alert("Oturum s√ºresi dolmu≈ü. L√ºtfen yeniden giri≈ü yapƒ±n.");
      navigate("/login");
      return;
    }

    const ticketData = {
      description,
      categoryId: parseInt(categoryId),
      priorityId: parseInt(priorityId),
    };

    try {
      const response = await axios.post("http://localhost:5094/api/Ticket/createTicket", ticketData, {
        headers: {
          Authorization: `Bearer ${token}`,
        }        
      });

      if (!response.data || !response.data.id) {
        throw new Error("Talep olu≈üturulamadƒ±.");
      }

      alert("Talep ba≈üarƒ±yla g√∂nderildi!");

      setTickets([...tickets, { id: response.data.id, description }]);

      setDescription("");
      setCategoryId("");
      setPriorityId("");
    } catch (error) {
      console.error("Hata:", error.message);
      alert("Bir hata olu≈ütu. L√ºtfen tekrar deneyin.");
    }
  };

  const ticketDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5094/api/Ticket/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setTickets(tickets.filter((t) => t.id !== id));
      setCompletedTickets(completedTickets.filter((t) => t.id !== id));
    } catch (error) {
      console.error("Silme hatasƒ±:", error.message);
    }
  };

  const markCompleted = (id) => {
    const selectedTicket = tickets.find((t) => t.id === id);
    if (selectedTicket) {
      setCompletedTickets([...completedTickets, selectedTicket]);
      setTickets(tickets.filter((t) => t.id !== id));
    }
  };

  const logout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("username");
    navigate("/login");
  };

  return (
    <div className="container">
      <div className="sidebar">
        <div className="logo">HD</div>
        <ul className="nav">
          <li>
            <span className="icon">üé´</span> <span className="label">Tickets</span>
            <span className="badge">99+</span>
          </li>
          <li>
            <span className="icon">üë•</span> <span className="label">Agents</span>
          </li>
          <li>
            <span className="icon">‚öôÔ∏è</span> <span className="label">Settings</span>
          </li>
        </ul>
        <div className="profile">
          <img src="https://i.pravatar.cc/40" alt="user" />
          <span>User</span>
        </div>
      </div>

      <div className="main">
        <div className="form-box">
          <h2>New Ticket</h2>
          <form>
            <label>Subject</label>
            <input type="text" placeholder="Enter subject" />

            <div className="row">
              <div>
                <label>Requester</label>
                <input type="text" placeholder="Full name" />
              </div>
              <div>
                <label>Email</label>
                <input type="email" placeholder="email@example.com" />
              </div>
            </div>

            <label>Team</label>
            <div className="team-box">
              <span>Support Heroes</span>
              <a href="#">Change</a>
            </div>

            <label>Agent</label>
            <div className="agent-box">
              <span>Unassigned</span>
              <a href="#">Assign</a>
            </div>

            <label>Message</label>
            <textarea rows="4" placeholder="Type a message..."></textarea>

            <button type="submit">Submit</button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default TicketForm;
