import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const TicketForm = () => {
  const [tickets, setTickets] = useState([]);
  const [completedTickets, setCompletedTickets] = useState([]);
  const [description, setDescription] = useState("");
  const [categoryId, setCategoryId] = useState("");
  const [priorityId, setPriorityId] = useState("");

  const navigate = useNavigate();

  const categories = [
    { categoryId: 1, name: "Yazılım Hatası" },
    { categoryId: 2, name: "Donanım Sorunu" },
    { categoryId: 3, name: "Kullanıcı Erişimi" },
    { categoryId: 4, name: "Ağ Problemleri" },
  ];

  const priorities = [
    { priorityId: 1, name: "Düşük" },
    { priorityId: 2, name: "Orta" },
    { priorityId: 3, name: "Yüksek" },
    { priorityId: 4, name: "Acil" },
  ];

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem("token");

    if (!token) {
      alert("Oturum süresi dolmuş. Lütfen yeniden giriş yapın.");
      return;
    }

    const ticketData = {
      description,
      categoryId: parseInt(categoryId),
      priorityId: parseInt(priorityId),
    };

    try {
      const response = await fetch("http://localhost:5094/api/Ticket/createTicket", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token.trim()}`,
        },
        body: JSON.stringify(ticketData),
      });

      if (!response.ok) {
        const errorText = await response.text();
        console.error("Sunucu Hatası:", errorText);
        throw new Error("Talep oluşturulamadı.");
      }

      const result = await response.json();
      alert("Talep başarıyla gönderildi!");

      setTickets([...tickets, { id: result.id || Date.now(), description }]);

      // Formu temizle
      setDescription("");
      setCategoryId("");
      setPriorityId("");
    } catch (error) {
      console.error("Hata:", error);
      alert("Bir hata oluştu. Lütfen tekrar deneyin.");
    }
  };

  const ticketDelete = (id) => {
    setTickets(tickets.filter((t) => t.id !== id));
    setCompletedTickets(completedTickets.filter((t) => t.id !== id));
  };

  const completed = (id) => {
    const doneTask = tickets.find((t) => t.id === id);
    if (doneTask) {
      setCompletedTickets([...completedTickets, doneTask]);
      setTickets(tickets.filter((t) => t.id !== id));
    }
  };

  const logout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("username");
    navigate("/login");
  };

  return (
    <form
      onSubmit={handleSubmit}
      className="max-w-md mx-auto p-6 bg-white shadow rounded-xl space-y-4"
    >
      <h2 className="text-xl font-bold text-center">Talep Oluştur</h2>

      <button onClick={logout} className="text-sm text-red-600 hover:underline">
        Çıkış Yap
      </button>

      <div>
        <label className="block text-sm font-medium">Açıklama</label>
        <textarea
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
          className="mt-1 block w-full border p-2 rounded"
        />
      </div>

      <div>
        <label className="block text-sm font-medium">Kategori</label>
        <select
          value={categoryId}
          onChange={(e) => setCategoryId(e.target.value)}
          required
          className="mt-1 block w-full border p-2 rounded"
        >
          <option value="" disabled>🗂️ Kategori seçin</option>
          {categories.map((cat) => (
            <option key={cat.categoryId} value={cat.categoryId}>
              {cat.name}
            </option>
          ))}
        </select>
      </div>

      <div>
        <label className="block text-sm font-medium">Öncelik</label>
        <select
          value={priorityId}
          onChange={(e) => setPriorityId(e.target.value)}
          required
          className="mt-1 block w-full border p-2 rounded"
        >
          <option value="" disabled>⚡ Öncelik seçin</option>
          {priorities.map((pri) => (
            <option key={pri.priorityId} value={pri.priorityId}>
              {pri.name}
            </option>
          ))}
        </select>
      </div>

      <button
        type="submit"
        className="w-full bg-blue-600 text-white py-2 px-4 rounded hover:bg-blue-700"
      >
        Gönder
      </button>

      <div>
        <h3 className="font-semibold">Aktif Görevler</h3>
        <ul>
          {tickets.map((g) => (
            <li key={g.id} className="flex justify-between items-center">
              {g.description}
              <div>
                <button onClick={() => completed(g.id)} className="text-green-600 mr-2">Tamamlandı</button>
                <button onClick={() => ticketDelete(g.id)} className="text-red-600">Sil</button>
              </div>
            </li>
          ))}
        </ul>
      </div>

      <div>
        <h3 className="font-semibold">Tamamlanan Görevler</h3>
        <ul>
          {completedTickets.map((g) => (
            <li key={g.id} className="flex justify-between items-center">
              <del>{g.description}</del>
              <button onClick={() => ticketDelete(g.id)} className="text-red-600">Sil</button>
            </li>
          ))}
        </ul>
      </div>
    </form>
  );
};

export default TicketForm;
