import React, { useState } from "react";

const TicketForm = () => {
  const [ticket, setTicket] = useState("");
  const [description, setDescription] = useState("");
  const [categoryId, setCategoryId] = useState("");
  const [priorityId, setPriorityId] = useState("");

  const categories = [
    { categoryId: 1, name: "Yazılım Hatası" },
    { categoryId: 2, name: "Donanım Sorunu" },
    { categoryId: 3, name: "Kullanıcı Erişimi" },
    { categoryId: 4, name: "Ağ Problemleri" },
  ];

  const priorities = [
    { priorityId: 1, name: "Düşük" },
    { priorityId: 2, name: "Orta" },
    { priorityId: 3, name: "Yüksek" },
    { priorityId: 4, name: "Acil" },
  ];

  const handleSubmit = async (e) => {
    e.preventDefault();

    const token = localStorage.getItem("token");

    if (!token) {
      alert("Oturum süresi dolmuş. Lütfen yeniden giriş yapın.");
      return;
    }

    const ticketData = {
      description,
      categoryId: parseInt(categoryId),
      priorityId: parseInt(priorityId),
    };

    console.log("Gönderilen Destek Talebi:", ticketData);

    try {
      const response = await fetch("http://localhost:5094/api/Ticket/createTicket", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token.trim()}`, 
        },
        body: JSON.stringify(ticketData),
      });

      if (!response.ok) {
        const errorText = await response.text();
        console.error("Sunucu Hatası:", errorText);
        throw new Error("Talep oluşturulamadı.");
      }

      const result = await response.json();
      console.log("Oluşturulan Talep:", result);
      alert("Talep başarıyla gönderildi!");

      setDescription("");
      setCategoryId("");
      setPriorityId("");
    } catch (error) {
      console.error("Hata:", error);
      alert("Bir hata oluştu. Lütfen tekrar deneyin.");
    }
  };

  const ticketAdd = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem("token");
    if (!token) {
      alert("Oturum süresi dolmuş. Lütfen yeniden giriş yapın.");
      return;
    }
    const ticketData = {
      name: ticket,
      description,
      categoryId: parseInt(categoryId),
      priorityId: parseInt(priorityId),
    };
    console.log("Gönderilen Destek Talebi:", ticketData);
    try {
      const response = await fetch("http://localhost:5094/api/Ticket/createTicket", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token.trim()}`,
        },
        body: JSON.stringify(ticketData),
      });
      if (!response.ok) {
        const errorText = await response.text();
        console.error("Sunucu Hatası:", errorText);
        throw new Error("Talep oluşturulamadı.");
      }
      const result = await response.json();
      console.log("Oluşturulan Talep:", result);
      alert("Talep başarıyla gönderildi!");
      setTicket("");
    } catch (error) {
      console.error("Hata:", error);
      alert("Bir hata oluştu. Lütfen tekrar deneyin.");
    }
  }

  const logout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("username");
    navigate("/login");
  };
  return (
    <form
      onSubmit={handleSubmit}
      className="max-w-md mx-auto p-6 bg-white shadow rounded-xl space-y-4"
    >
      <h2 className="text-xl font-bold text-center">Talep Oluştur</h2>

      <h2>Yapılacaklar</h2>
      <button onClick={logout}>Çıkış Yap</button>

      <div>
        <input
          type="text"
          placeholder="Yeni görev"
          value={ticket}
          onChange={(e) => setTicket(e.target.value)}
        />
        <button onClick={ticketAdd}>Ekle</button>
      </div>

      <div>
        <h3>Aktif Görevler</h3>
        <ul>
          {tickets.map((g) => (
            <li key={g.id}>
              {g.name}
              <button onClick={() => completed(g.id)}>Tamamlandı</button>
              <button onClick={() => ticketDelete(g.id)}>Sil</button>
            </li>
          ))}
        </ul>
      </div>

      <div>
        <h3>Tamamlanan Görevler</h3>
        <ul>
          {completedtickets.map((g) => (
            <li key={g.id}>
              <del>{g.name}</del>
              <button onClick={() => ticketDelete(g.id)}>Sil</button>
            </li>
          ))}
        </ul>
      </div>
      <div>
        <label className="block text-sm font-medium">Açıklama</label>
        <textarea
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
          className="mt-1 block w-full border p-2 rounded"
        />
      </div>

      <div>
        <label className="block text-sm font-medium">Kategori</label>
        <select
          value={categoryId}
          onChange={(e) => setCategoryId(e.target.value)}
          required
          className="mt-1 block w-full border p-2 rounded"
        >
          <option value="" disabled>🗂️ Kategori seçin</option>
          {categories.map((cat) => (
            <option key={cat.categoryId} value={cat.categoryId}>
              {cat.name}
            </option>
          ))}
        </select>
      </div>

      <div>
        <label className="block text-sm font-medium">Öncelik</label>
        <select
          value={priorityId}
          onChange={(e) => setPriorityId(e.target.value)}
          required
          className="mt-1 block w-full border p-2 rounded"
        >
          <option value="" disabled>⚡ Öncelik seçin</option>
          {priorities.map((pri) => (
            <option key={pri.priorityId} value={pri.priorityId}>
              {pri.name}
            </option>
          ))}
        </select>
      </div>

      <button
        type="submit"
        className="w-full bg-blue-600 text-white py-2 px-4 rounded hover:bg-blue-700"
      >
        Gönder
      </button>
    </form>
  );
};

export default TicketForm;
