import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Dashboard.css";
import Sidebar from "../Components/SideBar";

const AdminSettingsPage = () => {
  const [categories, setCategories] = useState([]);
  const [priorities, setPriorities] = useState([]);
  const [newCategory, setNewCategory] = useState("");
  const [newPriority, setNewPriority] = useState({ name: "", level: 1 });

  const token = localStorage.getItem("token"); // JWT varsa
  const headers = {
    Authorization: `Bearer ${token}`,
  };

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    const [catRes, priRes] = await Promise.all([
      axios.get("/api/ticket/categories", { headers }),
      axios.get("/api/ticket/priorities", { headers }),
    ]);
    setCategories(catRes.data);
    setPriorities(priRes.data);
  };

  const handleAddCategory = async () => {
    if (!newCategory.trim()) return;
    await axios.post(
      "/api/ticket/categories",
      { categoryName: newCategory },
      { headers }
    );
    setNewCategory("");
    fetchData();
  };

  const handleDeleteCategory = async (id) => {
    await axios.delete(`/api/ticket/categories/${id}`, { headers });
    fetchData();
  };

  const handleAddPriority = async () => {
    if (!newPriority.name.trim()) return;
    await axios.post(
      "/api/ticket/priorities",
      {
        priorityName: newPriority.name,
        priorityLevel: Number(newPriority.level),
      },
      { headers }
    );
    setNewPriority({ name: "", level: 1 });
    fetchData();
  };

  const handleDeletePriority = async (id) => {
    await axios.delete(`/api/ticket/priorities/${id}`, { headers });
    fetchData();
  };

  return (
    <div className="container">
      <Sidebar role={role} username={username} />
    <div className="admin-settings-page">
      <h2>⚙️ Ayarlar</h2>

      <div className="section">
        <h3>Kategoriler</h3>
        <ul>
          {categories.map((cat) => (
            <li key={cat.categoryId}>
              {cat.categoryName}
              <button onClick={() => handleDeleteCategory(cat.categoryId)}>Sil</button>
            </li>
          ))}
        </ul>
        <div className="input-row">
          <input
            type="text"
            placeholder="Yeni kategori"
            value={newCategory}
            onChange={(e) => setNewCategory(e.target.value)}
          />
          <button onClick={handleAddCategory}>Ekle</button>
        </div>
      </div>

      <div className="section">
        <h3>Öncelikler</h3>
        <ul>
          {priorities.map((pri) => (
            <li key={pri.priorityId}>
              {pri.priorityName} (Seviye: {pri.priorityLevel})
              <button onClick={() => handleDeletePriority(pri.priorityId)}>Sil</button>
            </li>
          ))}
        </ul>
        <div className="input-row">
          <input
            type="text"
            placeholder="Yeni öncelik"
            value={newPriority.name}
            onChange={(e) =>
              setNewPriority({ ...newPriority, name: e.target.value })
            }
          />
          <input
            type="number"
            placeholder="Seviye"
            value={newPriority.level}
            onChange={(e) =>
              setNewPriority({ ...newPriority, level: e.target.value })
            }
          />
          <button onClick={handleAddPriority}>Ekle</button>
        </div>
      </div>
    </div>
    </div>
  );
};

export default AdminSettingsPage;
