import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './TicketDetailView.css';

const TicketDetailView = ({ ticketId, isSupportUser }) => {
  const [ticket, setTicket] = useState(null);
  const [responseText, setResponseText] = useState('');
  const [responses, setResponses] = useState([]);

  useEffect(() => {
    const fetchTicket = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get(`http://localhost:5094/api/ticket/${ticketId}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setTicket(res.data);

        const responseRes = await await axios.post(`http://localhost:5094/api/ticket/${ticketId}/responses`, 
            JSON.stringify({ response: responseText }), 
            {
              headers: {
                Authorization: `Bearer ${token}`,
                'Content-Type': 'application/json'
              }
            }
          );

      } catch (err) {
        console.error("Ticket detaylarƒ± alƒ±namadƒ±:", err);
      }
    };

    fetchTicket();
  }, [ticketId]);

  const handleSubmitResponse = async () => {
  try {
    const token = localStorage.getItem('token');
    await axios.post(
      `http://localhost:5094/api/ticket/${ticketId}/responses`, 
      responseText, 
      {
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json' 
        }
      }
    );
    setResponseText('');
    const responseRes = await axios.get(`http://localhost:5094/api/ticket/${ticketId}/responses`, {
      headers: { Authorization: `Bearer ${token}` }
    });
    setResponses(responseRes.data);
  } catch (err) {
    console.error("Yanƒ±t g√∂nderilemedi:", err);
  }
};


  if (!ticket) return <div>Y√ºkleniyor...</div>;

  return (
    <div className="container">
      <div className="sidebar">
        <div className="logo">HelpDesk</div>
        <ul className="nav">
          <li><span className="icon">üé´</span> <span className="label">Tickets</span></li>
          <li><span className="icon">üë•</span> <span className="label">Agents</span></li>
          <li><span className="icon">‚öôÔ∏è</span> <span className="label">Settings</span></li>
        </ul>
        <div className="profile">
          <img src="https://i.pravatar.cc/40" alt="user" />
          <span>User</span>
        </div>
      </div>

      <div className="ticket-detail-area">
        <h2>Ticket Detaylarƒ±</h2>
        <p><strong>A√ßƒ±klama:</strong> {ticket.description}</p>
        <p><strong>Kategori:</strong> {ticket.category}</p>
        <p><strong>√ñncelik:</strong> {ticket.priority}</p>
        <p><strong>Olu≈üturulma Tarihi:</strong> {ticket.createdAt ? new Date(ticket.createdAt).toLocaleString() : "Tarih yok"}</p>
        <p><strong>Durum:</strong> {ticket.status}</p>

        <h3>Yanƒ±tlar</h3>
        {responses.length === 0 ? (
          <p>Hen√ºz yanƒ±t yok.</p>
        ) : (
          <ul className="response-list">
            {responses.map((res, index) => (
              <li key={index}>
                <strong>Kullanƒ±cƒ± {res.userId}:</strong> {res.response} <br />
                <small>{new Date(res.dateTime).toLocaleString()}</small>
              </li>
            ))}
          </ul>
        )}

        {(isSupportUser || ticket.status === 'Yanƒ±tlandƒ±') && (
          <div className="response-form">
            <h3>Yanƒ±t Ver</h3>
            <textarea
              rows="4"
              value={responseText}
              onChange={(e) => setResponseText(e.target.value)}
              placeholder="Yanƒ±tƒ±nƒ±zƒ± yazƒ±nƒ±z..."
            />
            <button onClick={handleSubmitResponse}>Yanƒ±t G√∂nder</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default TicketDetailView;
