import React, { useEffect, useState } from 'react';
import axios from 'axios';
import TicketDetailView from './Components/TicketDetailView';
import './SupportDashboard.css';
import { useNavigate } from 'react-router-dom';

const SupportDashboard = () => {
  const [tickets, setTickets] = useState([]);
  const [selectedTicketId, setSelectedTicketId] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchTickets = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get('http://localhost:5094/api/destek', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setTickets(res.data);
      } catch (err) {
        console.error('Ticketlar alÄ±namadÄ±:', err);
      }
    };

    fetchTickets();
  }, []);

  return (
    <div className="support-container">
      <div className="sidebar">
        <div className="logo">HelpDesk</div>
        <ul className="nav">
          <li onClick={() => navigate('/ticket-list')} style={{ cursor: 'pointer' }}>
            <span className="icon">ðŸŽ«</span> <span className="label">Tickets</span>
          </li>
        </ul>
        <div className="profile">
          <img src="https://i.pravatar.cc/40" alt="support" />
          <span>Destek KullanÄ±cÄ±sÄ±</span>
        </div>
      </div>

      <div className="main-content">
        <h2>Gelen Ticketlar</h2>
        <ul className="ticket-list">
          {tickets.map(ticket => (
            <li key={ticket.id} onClick={() => setSelectedTicketId(ticket.id)}>
              <strong>{ticket.description}</strong><br />
              Kategori: {ticket.category} - Ã–ncelik: {ticket.priority}
            </li>
          ))}
        </ul>
      </div>

      {/* SaÄŸdan kayan ticket detay paneli */}
      {selectedTicketId && (
        <div className="slide-panel">
          <button className="close-btn" onClick={() => setSelectedTicketId(null)}>X</button>
          <TicketDetailView ticketId={selectedTicketId} isSupportUser={true} />
        </div>
      )}
    </div>
  );
};

export default SupportDashboard;
