import { useAuth } from "../context/AuthContext";

const MessageBubble = ({ message }) => {
  const { user } = useAuth();

  let formattedDate = "Tarih yok";
  if (message.dateTime) {
    const date = new Date(message.dateTime);
    formattedDate = isNaN(date.getTime())
      ? "Geçersiz tarih"
      : date.toLocaleString();
  }

  const isOwnMessage = user?.userName === message.userName;

  return (
    <div
      className={`p-2 rounded border max-w-[70%] ${
        isOwnMessage ? "bg-blue-100 self-end text-right" : "bg-gray-100 self-start"
      }`}
    >
      <div className="text-sm font-semibold text-gray-700 mb-1">
        {message.userName || "Bilinmeyen Kullanıcı"}
      </div>
      <div className="text-sm">{message.response}</div>
      <div className="text-xs text-gray-500 mt-1">{formattedDate}</div>
    </div>
  );
};

export default MessageBubble;
