import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
//import './TicketResponse.css';

const TicketResponse = () => {
  const { ticketId } = useParams();
  const [ticket, setTicket] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  useEffect(() => {
    const fetchTicketDetails = async () => {
      try {
        const token = localStorage.getItem('token');
        const ticketRes = await axios.get(`http://localhost:5094/api/ticket/${ticketId}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setTicket(ticketRes.data);

        const messagesRes = await axios.get(`http://localhost:5094/api/ticket/${ticketId}/messages`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setMessages(messagesRes.data);
      } catch (error) {
        console.error('Veriler alınamadı:', error);
      }
    };

    fetchTicketDetails();
  }, [ticketId]);

  const handleSendMessage = async () => {
    if (!newMessage.trim()) return;

    try {
      const token = localStorage.getItem('token');
      const res = await axios.post(`http://localhost:5094/api/ticket/${ticketId}/messages`, {
        content: newMessage
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });

      setMessages(prev => [...prev, res.data]);
      setNewMessage('');
    } catch (error) {
      console.error('Mesaj gönderilemedi:', error);
    }
  };

  if (!ticket) return <div>Yükleniyor...</div>;

  return (

    <div className="chat-page">

      <h2>Ticket: {ticket.title}</h2>
      <p><strong>Açıklama:</strong> {ticket.description}</p>
      <p><strong>Kategori:</strong> {ticket.category} - <strong>Öncelik:</strong> {ticket.priority}</p>

      <div className="chat-box">
        {messages.map((msg, index) => (
          <div key={index} className={`message ${msg.isFromSupport ? 'support' : 'user'}`}>
            <div className="message-content">{msg.content}</div>
            <div className="message-meta">{msg.senderName} - {new Date(msg.timestamp).toLocaleString()}</div>
          </div>
        ))}
      </div>

      <div className="message-input">
        <textarea
          rows="3"
          placeholder="Yanıtınızı yazın..."
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
        />
        <button onClick={handleSendMessage}>Gönder</button>
      </div>
    </div>
  );
};

export default TicketResponse;
