import { useParams, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";
import ResponseBox from "../Components/ResponseBox";
import "./SupportDashboard.css"; // Aynı stilleri kullanmak için
import Sidebar from '../Components/Sidebar'; 

const TicketDetailPage = () => {
  const { ticketId } = useParams();
  const [ticket, setTicket] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchTicketDetail = async () => {
      try {
        const token = localStorage.getItem("token");
        const res = await axios.get(`http://localhost:5094/api/ticket/${ticketId}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setTicket(res.data);
      } catch (err) {
        console.error("Ticket detay alınamadı", err);
      }
    };

    fetchTicketDetail();
  }, [ticketId]);

  if (!ticket) return <div>Yükleniyor...</div>;

  return (
    <div className="container" style={{ display: 'flex' }}>
      <Sidebar /> {/* tekrar yazmak yerine sadece çağırıyoruz */}


      {/* Main Content */}
      <div className="main-content">
        <div className="form-box">
          <h2 className="text-2xl font-bold mb-2">Ticket Başlığı: {ticket.title}</h2>
          <p className="mb-4">{ticket.description}</p>

          <h3 className="text-lg font-semibold mb-2">Mesajlar</h3>
          <ResponseBox ticketId={ticketId} />
        </div>
      </div>
    </div>
  );
};

export default TicketDetailPage;
