import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import LoginPage from "./LoginPage.js";
import RegisterPage from "./RegisterPage.js";
import MainPage from "./MainPage.js";
import CreateTicket from "./CreateTicket.js"; 
import ListTicket from "./ListTickets.js";
import TicketDetailWrapper from "./TicketDetailWrapper";
import SupportDashboard from "./SupportDashboard.js";

import 'ag-grid-community/styles/ag-grid.css';
import 'ag-grid-community/styles/ag-theme-alpine.css';

function App() {
  // localStorage'dan user bilgisi alınır
  const user = JSON.parse(localStorage.getItem("user"));

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route path="/" element={<MainPage />} />
          <Route path="/login" element={<LoginPage />} />
          <Route path="/register" element={<RegisterPage />} />
          <Route path="/tickets" element={<CreateTicket />} /> 
          <Route path="/ticket-list" element={<ListTicket />} />
          <Route path="/ticket/:ticketId" element={<TicketDetailWrapper />} />

          {/* Destek kullanıcısı için yönlendirme */}
          <Route 
            path="/destek" 
            element={
              user?.role === 'Support' 
                ? <SupportDashboard /> 
                : <Navigate to="/" replace />
            } 
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
