import { useAuth } from "../context/AuthContext";

const MessageBubble = ({ message }) => {
  const { user } = useAuth();
  const isMe = message.createdBy === user.id;

  return (
    <div className={`flex ${isMe ? "justify-end" : "justify-start"}`}>
      <div
        className={`max-w-[60%] px-3 py-2 rounded-lg ${
          isMe ? "bg-blue-500 text-white" : "bg-gray-200 text-black"
        }`}
      >
        <p className="text-sm">{message.responseText}</p>
        <p className="text-[10px] text-gray-600 mt-1 text-right">
          {new Date(message.createdAt).toLocaleString()}
        </p>
      </div>
    </div>
  );
};

export default MessageBubble;
