import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './SupportDashboard.css';
import { useNavigate } from 'react-router-dom';
import "./TicketPage.css";

const AdminDashboard = () => {
  const [tickets, setTickets] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchTickets = async () => {
      try {
        const token = localStorage.getItem('token');

        if (!token) {
          console.error('Token bulunamadı. Giriş yapmanız gerekiyor.');
          return;
        }

        const res = await axios.get('http://localhost:5094/api/ticket/getAllTicketsByRole', {
          headers: { Authorization: `Bearer ${token}` }
        });

        setTickets(res.data);
      } catch (err) {
        console.error('Ticketlar alınamadı:', err);
        alert('Ticketlar yüklenirken bir hata oluştu. Lütfen tekrar deneyin.');
      }
    };

    fetchTickets();
  }, []);

  return (
    <div className="support-container">
      {/* Sidebar */}
      <div className="sidebar">
        <div className="logo">HelpDesk Admin</div>
        <ul className="nav">
          <li onClick={() => navigate('/admin-dashboard')} style={{ cursor: 'pointer' }}>
            <span className="icon">📋</span> <span className="label">Kategori / Durum</span>
          </li>
          <li onClick={() => navigate('/ticket-list')} style={{ cursor: 'pointer' }}>
            <span className="icon">🎫</span> <span className="label">Tickets</span>
          </li>
        </ul>

        <div className="profile">
          <img src="https://i.pravatar.cc/40" alt="admin" />
          <span>Admin Kullanıcısı</span>
        </div>
      </div>

      {/* Main Content */}
      <div className="main-content">
        <div className="admin-controls white-card">
          <h2>Kategori, Öncelik ve Durum Yönetimi</h2>
          <div style={{ display: 'flex', gap: '10px', marginBottom: '20px' }}>
            <button className="reply-button" onClick={() => navigate('/manage-categories')}>Kategoriler</button>
            <button className="reply-button" onClick={() => navigate('/manage-priorities')}>Öncelikler</button>
            <button className="reply-button" onClick={() => navigate('/manage-statuses')}>Durumlar</button>
          </div>
        </div>

        <div className="ticket-section white-card">
          <h3>Gelen Ticketlar</h3>
          <ul className="ticket-list">
            {tickets.map(ticket => (
              <li
                key={ticket.id}
                onClick={() => navigate(`/ticket/${ticket.id}`)}
                style={{ cursor: 'pointer' }}
              >
                <strong>{ticket.description}</strong><br />
                Kategori: {ticket.category} - Öncelik: {ticket.priority}
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
