import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import ResponseBox from "../Components/ResponseBox";

const TicketDetails = () => {
  const { ticketId } = useParams();
  const [ticket, setTicket] = useState(null);
  const token = localStorage.getItem("token");

  useEffect(() => {
    const fetchTicketDetail = async () => {
      try {
        const res = await fetch(`http://localhost:5094/api/Ticket/${ticketId}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (!res.ok) throw new Error("Detay alınamadı");

        const data = await res.json();
        setTicket(data);
      } catch (err) {
        console.error(err);
      }
    };

    fetchTicketDetail();
  }, [ticketId, token]);

  if (!ticket) {
    return <div>Yükleniyor...</div>;
  }

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">Ticket Detayları</h2>
      <div className="mb-6 bg-gray-100 p-4 rounded shadow">
        <p><strong>ID:</strong> {ticket.id}</p>
        <p><strong>Açıklama:</strong> {ticket.description}</p>
        <p><strong>Durum:</strong> {ticket.status}</p>
        <p><strong>Kategori:</strong> {ticket.category}</p>
        <p><strong>Öncelik:</strong> {ticket.priority}</p>
        <p><strong>Oluşturulma:</strong> {new Date(ticket.createdAt).toLocaleString("tr-TR")}</p>
      </div>

      <h3 className="text-xl font-semibold mb-2">Yanıtlar</h3>
      <ResponseBox ticketId={ticketId} />
    </div>
  );
};

export default TicketDetails;
