import React, { useEffect, useState, useCallback } from "react";
import { useNavigate } from "react-router-dom";
import { AgGridReact } from "ag-grid-react";
import "ag-grid-community/styles/ag-grid.css";
import "ag-grid-community/styles/ag-theme-alpine.css";
import { ClientSideRowModelModule } from "@ag-grid-community/client-side-row-model";
import { ModuleRegistry } from "@ag-grid-community/core";

ModuleRegistry.registerModules([ClientSideRowModelModule]);

const ListTicket = () => {
  const [rowData, setRowData] = useState([]);
  const navigate = useNavigate();
  const token = localStorage.getItem("token");
  const role = localStorage.getItem("role");

  const fetchTickets = useCallback(() => {
    fetch(`http://localhost:5094/api/Ticket/getAllTicketsByRole`, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    })
      .then(res => {
        if (res.status === 403) {
          throw new Error("Yetkisiz erişim");
        }
        return res.json();
      })
      .then(data => setRowData(data))
      .catch(err => {
        console.error("Ticket verisi çekilirken hata oluştu:", err);
        alert("Ticket verisi alınamadı veya yetkiniz yok.");
        navigate("/todos");
      });
  }, [token, navigate]);

  useEffect(() => {
    if (!token) {
      navigate("/login");
    } else if (role !== "Admin" && role !== "Destek") {
      alert("Bu sayfaya erişim izniniz yok.");
      navigate("/todos");
    } else {
      fetchTickets();
    }
  }, [token, role, fetchTickets, navigate]);

  const columnDefs = [
    { headerName: "ID", field: "ticketId" },
    { headerName: "Kullanıcı ID", field: "userId" },
    { headerName: "Açıklama", field: "description" },
    { headerName: "Kategori ID", field: "categoryId" },
    { headerName: "Öncelik ID", field: "priorityId" },
    { headerName: "Tarih", field: "dateTime" },
    { headerName: "Tamamlandı", field: "isCompleted", cellRenderer: params => (params.value ? "Evet" : "Hayır") }
  ];

  return (
    <div className="ag-theme-alpine" style={{ height: 600, width: "100%" }}>
      <AgGridReact
        rowData={rowData}
        columnDefs={columnDefs}
        pagination={true}
        paginationPageSize={20}
      />
    </div>
  );
};

export default ListTicket;
