import React from "react";
import { useNavigate } from "react-router-dom";
import "./Sidebar.css";

const Sidebar = ({ role, username }) => {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.clear(); // Token, username, role vs. hepsini temizle
    navigate("/");
  };

  return (
    <div className="sidebar">
      <div className="logo">HelpDesk</div>

      <ul className="nav">
        <li onClick={() => navigate("/ticket-list")}>
          <span className="icon">üé´</span> <span className="label">Tickets</span>
        </li>

        {(role === "Admin" || role === "Destek") && (
          <li onClick={() => navigate("/reports")}>
            <span className="icon">üìä</span> <span className="label">Raporlar</span>
          </li>
        )}

        {role === "Admin" && (
          <>
            <li onClick={() => navigate("/agents")}>
              <span className="icon">üë•</span> <span className="label">Agents</span>
            </li>
            <li onClick={() => navigate("/settings")}>
              <span className="icon">‚öôÔ∏è</span> <span className="label">Settings</span>
            </li>
            <li onClick={() => navigate("/manage-settings")}>
              <span className="icon">üß©</span> <span className="label">Kategori & √ñncelik</span>
            </li>
          </>
        )}
      </ul>

      <div className="profile">
        <img src="https://i.pravatar.cc/40" alt="user" />
        <span>{username}</span>
        <button onClick={handleLogout} className="logout-button">√áƒ±kƒ±≈ü Yap</button>
      </div>
    </div>
  );
};

export default Sidebar;
