          </li>
          <li onClick={() => navigate("/reports")}>
            <span className="icon">ðŸ“Š</span> <span className="label">Raporlar</span>
          </li>
        </ul>
        <div className="profile">
          <img src="https://i.pravatar.cc/40" alt="admin" />
          <span>Admin</span>
        </div>
      </div>
      <h2>ðŸ“Š Raporlar</h2>

      <h3>En Ã‡ok Ticket Gelen Kategoriler</h3>
      {categoryStats.length > 0 ? (
        <PieChart width={400} height={300}>
          <Pie
            data={categoryStats}
            dataKey="ticketCount"
            nameKey="categoryName"
            cx="50%"
            cy="50%"
            outerRadius={100}
            fill="#8884d8"
            label
          >
            {categoryStats.map((entry, index) => (
              <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
            ))}
          </Pie>
          <Tooltip />
          <Legend />
        </PieChart>
      ) : (
        <p>YÃ¼kleniyor...</p>
      )}

      <h3>Ã‡Ã¶zÃ¼m OranlarÄ±</h3>
      {resolutionStats && (
        <div>
          <p>Toplam Ticket: {resolutionStats.totalTickets}</p>
          <p>Ã‡Ã¶zÃ¼len Ticket: {resolutionStats.resolvedTickets}</p>
          <p>Ã‡Ã¶zÃ¼m OranÄ±: %{resolutionStats.resolutionRate.toFixed(2)}</p>
        </div>
      )}
    </div>
  );
};

export default ReportPage;
