import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { AgGridReact } from "ag-grid-react";
import "ag-grid-community/styles/ag-grid.css";
import "ag-grid-community/styles/ag-theme-alpine.css";
import { ClientSideRowModelModule } from 'ag-grid-community';
import { jwtDecode } from "jwt-decode";
import "./TicketPage.css";

const API_URL = "http://localhost:5094/api/Ticket";

const TicketForm = () => {
  const [tickets, setTickets] = useState([]);
  const [description, setDescription] = useState("");
  const [categoryId, setCategoryId] = useState("");
  const [priorityId, setPriorityId] = useState("");
  const [columnDefs] = useState([
    { headerName: "ID", field: "ticketId", width: 100 },
    { headerName: "A√ßƒ±klama", field: "description", flex: 1 },
    { headerName: "Kategori", field: "categoryName", flex: 1 },
    { headerName: "√ñncelik", field: "priorityName", flex: 1 },
    { headerName: "Durum", field: "isResolved", flex: 1,
      cellRenderer: (params) => (params.value ? "Tamamlandƒ±" : "Bekliyor") },
    {
      headerName: "Aksiyonlar",
      cellRenderer: (params) => (
        <div>
          {!params.data.isResolved && (
            <button onClick={() => markAsResolved(params.data.ticketId)}>Tamamla</button>
          )}
          <button onClick={() => deleteTicket(params.data.ticketId)}>Sil</button>
        </div>
      ),
      width: 200,
    },
  ]);

  const navigate = useNavigate();
  const token = localStorage.getItem("token");

  const fetchTickets = useCallback(async () => {
    if (!token) return;
    try {
      const response = await axios.get(`${API_URL}/mytickets`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTickets(response.data);
    } catch (error) {
      console.error("Ticketlar alƒ±nƒ±rken hata:", error);
    }
  }, [token]);

  useEffect(() => {
  if (!token) {
    console.warn("Token bulunamadƒ±, login sayfasƒ±na y√∂nlendiriliyor.");
    navigate("/login");
  } else {
    console.log("Token:", token);
    fetchTickets();
  }
}, [token, fetchTickets, navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const ticketData = {
      description,
      categoryId: parseInt(categoryId),
      priorityId: parseInt(priorityId),
    };

    try {
      await axios.post(`${API_URL}/createTicket`, ticketData, {
        headers: { Authorization: `Bearer ${token}` },
      });

      alert("Talep ba≈üarƒ±yla g√∂nderildi!");
      setDescription("");
      setCategoryId("");
      setPriorityId("");
      fetchTickets(); // Yeni ticket eklendikten sonra listeyi yenile
    } catch (error) {
      console.error("Hata:", error.message);
      alert("Bir hata olu≈ütu. L√ºtfen tekrar deneyin.");
    }
  };

  const markAsResolved = async (id) => {
    try {
      await axios.put(`${API_URL}/${id}`, { isResolved: true }, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchTickets();
    } catch (error) {
      console.error("Tamamlama hatasƒ±:", error.message);
    }
  };

  const deleteTicket = async (id) => {
    try {
      await axios.delete(`${API_URL}/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchTickets();
    } catch (error) {
      console.error("Silme hatasƒ±:", error.message);
    }
  };

  const categories = [
    { categoryId: 1, name: "Yazƒ±lƒ±m Hatasƒ±" },
    { categoryId: 2, name: "Donanƒ±m Sorunu" },
    { categoryId: 3, name: "Kullanƒ±cƒ± Eri≈üimi" },
    { categoryId: 4, name: "Aƒü Problemleri" },
  ];

  const priorities = [
    { priorityId: 1, name: "D√º≈ü√ºk" },
    { priorityId: 2, name: "Orta" },
    { priorityId: 3, name: "Y√ºksek" },
    { priorityId: 4, name: "Acil" },
  ];

  return (
    <div className="container">
      <div className="sidebar">
        <div className="logo">HD</div>
        <ul className="nav">
          <li>
            <span className="icon">üé´</span> <span className="label">Tickets</span>
          </li>
          <li>
            <span className="icon">üë•</span> <span className="label">Agents</span>
          </li>
          <li>
            <span className="icon">‚öôÔ∏è</span> <span className="label">Settings</span>
          </li>
        </ul>
        <div className="profile">
          <img src="https://i.pravatar.cc/40" alt="user" />
          <span>User</span>
        </div>
      </div>

      <div className="main">
        <div className="form-box">
          <h2>Yeni Talep Olu≈ütur</h2>
          <form onSubmit={handleSubmit}>
            <label>A√ßƒ±klama</label>
            <textarea
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              placeholder="Talep a√ßƒ±klamasƒ±nƒ± yazƒ±nƒ±z..."
              rows="4"
              required
            />

            <label>Kategori</label>
            <select value={categoryId} onChange={(e) => setCategoryId(e.target.value)} required>
              <option value="">Kategori Se√ßin</option>
              {categories.map((cat) => (
                <option key={cat.categoryId} value={cat.categoryId}>{cat.name}</option>
              ))}
            </select>

            <label>√ñncelik</label>
            <select value={priorityId} onChange={(e) => setPriorityId(e.target.value)} required>
              <option value="">√ñncelik Se√ßin</option>
              {priorities.map((pri) => (
                <option key={pri.priorityId} value={pri.priorityId}>{pri.name}</option>
              ))}
            </select>

            <button type="submit">Talep G√∂nder</button>
          </form>
        </div>

        <div className="ticket-list">
          <h2>Mevcut Talepler</h2>
          <div className="ag-theme-alpine" style={{ height: 400, width: "100%" }}>
            <AgGridReact
              rowData={tickets}
              columnDefs={columnDefs}
              pagination={true}
              paginationPageSize={10}
              domLayout="autoHeight"
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default TicketForm;
