import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const TicketForm = () => {
  const [tickets, setTickets] = useState([]);
  const [completedTickets, setCompletedTickets] = useState([]);
  const [description, setDescription] = useState("");
  const [categoryId, setCategoryId] = useState("");
  const [priorityId, setPriorityId] = useState("");
  const navigate = useNavigate();
  const token = localStorage.getItem("token");

  useEffect(() => {
    if (!token) {
      navigate("/login");
    }
  }, [token, navigate]);

  const categories = [
    { categoryId: 1, name: "Yazƒ±lƒ±m Hatasƒ±" },
    { categoryId: 2, name: "Donanƒ±m Sorunu" },
    { categoryId: 3, name: "Kullanƒ±cƒ± Eri≈üimi" },
    { categoryId: 4, name: "Aƒü Problemleri" },
  ];

  const priorities = [
    { priorityId: 1, name: "D√º≈ü√ºk" },
    { priorityId: 2, name: "Orta" },
    { priorityId: 3, name: "Y√ºksek" },
    { priorityId: 4, name: "Acil" },
  ];

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!token) {
      alert("Oturum s√ºresi dolmu≈ü. L√ºtfen yeniden giri≈ü yapƒ±n.");
      navigate("/login");
      return;
    }

    const ticketData = {
      description,
      categoryId: parseInt(categoryId),
      priorityId: parseInt(priorityId),
    };

    try {
      const response = await axios.post("http://localhost:5094/api/Ticket/createTicket", ticketData, {
        headers: {
          Authorization: `Bearer ${token}`,
        }        
      });

      if (!response.data || !response.data.id) {
        throw new Error("Talep olu≈üturulamadƒ±.");
      }

      alert("Talep ba≈üarƒ±yla g√∂nderildi!");

      setTickets([...tickets, { id: response.data.id, description }]);

      setDescription("");
      setCategoryId("");
      setPriorityId("");
    } catch (error) {
      console.error("Hata:", error.message);
      alert("Bir hata olu≈ütu. L√ºtfen tekrar deneyin.");
    }
  };

  const ticketDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5094/api/Ticket/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setTickets(tickets.filter((t) => t.id !== id));
      setCompletedTickets(completedTickets.filter((t) => t.id !== id));
    } catch (error) {
      console.error("Silme hatasƒ±:", error.message);
    }
  };

  const markCompleted = (id) => {
    const selectedTicket = tickets.find((t) => t.id === id);
    if (selectedTicket) {
      setCompletedTickets([...completedTickets, selectedTicket]);
      setTickets(tickets.filter((t) => t.id !== id));
    }
  };

  const logout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("username");
    navigate("/login");
  };

  return (
    <form onSubmit={handleSubmit} className="max-w-md mx-auto p-6 bg-white shadow rounded-xl space-y-4">
      {/* Paket Y√∂neticisi Benzeri G√∂r√ºn√ºm */}
<div className="bg-gray-100 border rounded-lg shadow-md mt-10 p-4">
  <h3 className="text-lg font-bold mb-2">Paket Y√∂neticisi</h3>

  {/* Arama √áubuƒüu */}
  <div className="mb-4">
    <input
      type="text"
      placeholder="üîç Ara..."
      className="w-full p-2 border border-gray-300 rounded"
    />
  </div>

  {/* Sol Filtre Men√ºs√º */}
  <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
    {/* Sol Men√º */}
    <div className="bg-white p-3 rounded shadow h-fit">
      <h4 className="font-semibold mb-2">Kategoriler</h4>
      <ul className="space-y-1 text-sm">
        <li><button className="text-blue-600 hover:underline">T√ºm√º</button></li>
        <li><button className="hover:underline">Yazƒ±lƒ±m</button></li>
        <li><button className="hover:underline">Donanƒ±m</button></li>
        <li><button className="hover:underline">G√ºvenlik</button></li>
      </ul>
    </div>

    {/* Paket Listesi */}
    <div className="md:col-span-3 bg-white p-4 rounded shadow">
      <h4 className="font-semibold mb-2">Paketler</h4>
      <ul className="divide-y text-sm">
        <li className="py-2">
          <div className="flex justify-between">
            <span>Unity UI Toolkit</span>
            <button className="text-blue-600 hover:underline">Y√ºkle</button>
          </div>
        </li>
        <li className="py-2">
          <div className="flex justify-between">
            <span>Visual Scripting</span>
            <button className="text-blue-600 hover:underline">Y√ºkle</button>
          </div>
        </li>
        <li className="py-2">
          <div className="flex justify-between">
            <span>Post Processing</span>
            <button className="text-blue-600 hover:underline">Y√ºkle</button>
          </div>
        </li>
      </ul>
    </div>
  </div>
</div>

    </form>
  );
};

export default TicketForm;
