import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { AgGridReact } from "ag-grid-react";
import "ag-grid-community/styles/ag-grid.css";
import "ag-grid-community/styles/ag-theme-alpine.css";
import { ClientSideRowModelModule } from 'ag-grid-community';
import { jwtDecode } from "jwt-decode";
import "./TicketPage.css";

const API_URL = "http://localhost:5094/api/Ticket";

const TicketForm = () => {
  const [tickets, setTickets] = useState([]);
  const [description, setDescription] = useState("");
  const [categoryId, setCategoryId] = useState("");
  const [priorityId, setPriorityId] = useState("");

  const navigate = useNavigate();
  const token = localStorage.getItem("token");

  const fetchTickets = useCallback(async () => {
    if (!token) return;
    try {
      const response = await axios.get(`${API_URL}/mytickets`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTickets(response.data);
    } catch (error) {
      console.error("Ticketlar alınırken hata:", error);
    }
  }, [token]);

  useEffect(() => {
    if (!token) {
      console.warn("Token bulunamadı, login sayfasına yönlendiriliyor.");
      navigate("/login");
    } else {
      fetchTickets();
    }
  }, [token, fetchTickets, navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const ticketData = {
      description,
      categoryId: parseInt(categoryId),
      priorityId: parseInt(priorityId),
    };

    try {
      await axios.post(`${API_URL}/createTicket`, ticketData, {
        headers: { Authorization: `Bearer ${token}` },
      });

      alert("Talep başarıyla gönderildi!");
      setDescription("");
      setCategoryId("");
      setPriorityId("");
      fetchTickets();
    } catch (error) {
      console.error("Hata:", error.message);
      alert("Bir hata oluştu. Lütfen tekrar deneyin.");
    }
  };

  const markAsResolved = async (id) => {
    try {
      await axios.put(`${API_URL}/${id}`, { isResolved: true }, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchTickets();
    } catch (error) {
      console.error("Tamamlama hatası:", error.message);
    }
  };

  const deleteTicket = async (id) => {
    try {
      await axios.delete(`${API_URL}/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchTickets();
    } catch (error) {
      console.error("Silme hatası:", error.message);
    }
  };

  const categories = [
    { categoryId: 1, name: "Yazılım Hatası" },
    { categoryId: 2, name: "Donanım Sorunu" },
    { categoryId: 3, name: "Kullanıcı Erişimi" },
    { categoryId: 4, name: "Ağ Problemleri" },
  ];

  const priorities = [
    { priorityId: 1, name: "Düşük" },
    { priorityId: 2, name: "Orta" },
    { priorityId: 3, name: "Yüksek" },
    { priorityId: 4, name: "Acil" },
  ];

  const columnDefs = [
    { headerName: "ID", field: "ticketId", width: 100 },
    { headerName: "Açıklama", field: "description", flex: 1 },
    { headerName: "Kategori", field: "categoryName", flex: 1 },
    { headerName: "Öncelik", field: "priorityName", flex: 1 },
    {
      headerName: "Durum", field: "isResolved", flex: 1,
      cellRenderer: (params) => (params.value ? "Tamamlandı" : "Bekliyor")
    },
    {
      headerName: "Aksiyonlar",
      cellRenderer: (params) => (
        <div>
          {!params.data.isResolved && (
            <button onClick={() => markAsResolved(params.data.ticketId)}>Tamamla</button>
          )}
          <button onClick={() => deleteTicket(params.data.ticketId)}>Sil</button>
        </div>
      ),
      width: 200,
    },
  ];

  return (
    <div className="ticket-page" style={{ display: "flex", gap: "20px", padding: "20px" }}>

      {/* Sol Form: Yeni Talep */}
      <div style={{ flex: 1 }}>
        <h2>Yeni Talep Oluştur</h2>
        <form onSubmit={handleSubmit}>
          <label>Açıklama</label>
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Talep açıklamasını yazınız..."
            rows="4"
            required
          />

          <label>Kategori</label>
          <select value={categoryId} onChange={(e) => setCategoryId(e.target.value)} required>
            <option value="">Kategori Seçin</option>
            {categories.map((cat) => (
              <option key={cat.categoryId} value={cat.categoryId}>{cat.name}</option>
            ))}
          </select>

          <label>Öncelik</label>
          <select value={priorityId} onChange={(e) => setPriorityId(e.target.value)} required>
            <option value="">Öncelik Seçin</option>
            {priorities.map((pri) => (
              <option key={pri.priorityId} value={pri.priorityId}>{pri.name}</option>
            ))}
          </select>

          <button type="submit">Talep Gönder</button>
        </form>
      </div>

      {/* Sağ Panel: Mevcut Talepler */}
      <div style={{ flex: 2 }}>
        <h2>Mevcut Talepler</h2>
        <div className="ag-theme-alpine" style={{ height: 500, width: "100%" }}>
          <AgGridReact
            rowData={tickets}
            columnDefs={columnDefs}
            pagination={true}
            paginationPageSize={10}
            domLayout="autoHeight"
          />
        </div>
      </div>
    </div>
  );
};

export default TicketForm;
