import { useParams, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";
import ResponseBox from "../Components/ResponseBox";
import "./SupportDashboard.css";

const TicketDetailPage = () => {
  const { ticketId } = useParams();
  const [ticket, setTicket] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchTicketDetail = async () => {
      try {
        const token = localStorage.getItem("token");
        const res = await axios.get(`http://localhost:5094/api/ticket/${ticketId}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setTicket(res.data);
      } catch (err) {
        console.error("Ticket detay alƒ±namadƒ±", err);
      }
    };

    fetchTicketDetail();
  }, [ticketId]);

  if (!ticket) return <div>Y√ºkleniyor...</div>;

  return (
    <div className="support-container">
      {/* Sidebar */}
      <div className="sidebar">
        <div className="logo">HelpDesk</div>
        <ul className="nav">
          <li onClick={() => navigate("/ticket-list")} style={{ cursor: "pointer" }}>
            <span className="icon">üé´</span> <span className="label">Tickets</span>
          </li>
          <li>
            <span className="icon">üë•</span> <span className="label">Agents</span>
          </li>
          <li>
            <span className="icon">‚öôÔ∏è</span> <span className="label">Settings</span>
          </li>
        </ul>
        <div className="profile">
          <img src="https://i.pravatar.cc/40" alt="user" />
          <span>Kullanƒ±cƒ±</span>
        </div>
      </div>

      {/* Main Content (chat + details split) */}
      <div className="flex flex-1 h-screen overflow-hidden">
        {/* Left: Message Panel */}
        <div className="flex-1 p-6 overflow-y-auto border-r">
          <h2 className="text-2xl font-bold mb-2">{ticket.title}</h2>
          <p className="mb-4 text-gray-700">{ticket.description}</p>

          <h3 className="text-lg font-semibold mb-2">Mesajlar</h3>
          <ResponseBox ticketId={ticketId} />
        </div>

        {/* Right: Ticket Details */}
        <div className="w-[350px] bg-gray-50 p-6 overflow-y-auto">
          <h3 className="text-lg font-semibold mb-4">Ticket Bilgileri</h3>
          <p><strong>ID:</strong> {ticket.id}</p>
          <p><strong>Olu≈üturulma:</strong> {new Date(ticket.createdAt).toLocaleString()}</p>
          <p><strong>Son Mesaj:</strong> {new Date(ticket.updatedAt).toLocaleString()}</p>
          <p><strong>Durum:</strong> {ticket.status}</p>
          <p><strong>Kaynak:</strong> {ticket.source}</p>
          <p><strong>Kategori:</strong> {ticket.category?.name || "Yok"}</p>
          <p><strong>√ñncelik:</strong> {ticket.priority?.name || "Belirtilmemi≈ü"}</p>

          <div className="mt-4">
            <h4 className="font-semibold">Sorumlu</h4>
            <p><strong>Agent:</strong> {ticket.agent?.name || "Atanmamƒ±≈ü"}</p>
            <p><strong>Email:</strong> {ticket.agent?.email}</p>
          </div>

          <div className="mt-4">
            <h4 className="font-semibold">Talep Eden</h4>
            <p><strong>Ad:</strong> {ticket.user?.name}</p>
            <p><strong>Email:</strong> {ticket.user?.email}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default TicketDetailPage;
