import React from "react";
import { useNavigate } from "react-router-dom";
import "./Sidebar.css";

const Sidebar = ({ role, username }) => {
  const navigate = useNavigate();

  return (
    <div className="sidebar">
      <div className="logo">HelpDesk</div>

      <ul className="nav">
        <li onClick={() => navigate("/ticket-list")}>
          <span className="icon">üé´</span> <span className="label">Tickets</span>
        </li>

        {/* "admin" ve "destek" rollerine √∂zel buton */}
        {(role === "admin" || role === "destek") && (
          <li onClick={() => navigate("/reports")}>
            <span className="icon">üìä</span> <span className="label">Raporlar</span>
          </li>
        )}

        {/* admin'e √∂zel butonlar */}
        {role === "admin" && (
          <>
            <li onClick={() => navigate("/agents")}>
              <span className="icon">üë•</span> <span className="label">Agents</span>
            </li>
            <li onClick={() => navigate("/settings")}>
              <span className="icon">‚öôÔ∏è</span> <span className="label">Settings</span>
            </li>
          </>
        )}
      </ul>

      <div className="profile">
        <img src="https://i.pravatar.cc/40" alt="user" />
        <span>{username}</span>
      </div>
    </div>
  );
};

export default Sidebar;
