import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './TicketDetailView.css';

const TicketDetailView = ({ ticketId, isSupportUser }) => {
  const [ticket, setTicket] = useState(null);
  const [responseText, setResponseText] = useState('');
  const [responses, setResponses] = useState([]);

  useEffect(() => {
    const fetchTicketData = async () => {
      try {
        const token = localStorage.getItem('token');
        const headers = { Authorization: `Bearer ${token}` };

        // Ticket ve yanıtları aynı anda getir
        const [ticketRes, responseRes] = await Promise.all([
          axios.get(`http://localhost:5094/api/ticket/${ticketId}`, { headers }),
          axios.get(`http://localhost:5094/api/ticket/${ticketId}/responses`, { headers })
        ]);

        setTicket(ticketRes.data);
        setResponses(responseRes.data);
      } catch (err) {
        console.error("Ticket detayları alınamadı:", err);
      }
    };

    fetchTicketData();
  }, [ticketId]);

  const handleSubmitResponse = async () => {
    if (!responseText.trim()) return;

    try {
      const token = localStorage.getItem('token');
      const headers = {
        Authorization: `Bearer ${token}`,
        'Content-Type': 'application/json'
      };

      await axios.post(
        `http://localhost:5094/api/ticket/${ticketId}/responses`,
        JSON.stringify({ response: responseText }),
        { headers }
      );

      setResponseText('');

      const responseRes = await axios.get(`http://localhost:5094/api/ticket/${ticketId}/responses`, { headers });
      setResponses(responseRes.data);
    } catch (err) {
      console.error("Yanıt gönderilemedi:", err);
    }
  };

  if (!ticket) return <div>Yükleniyor...</div>;

  return (
    <div className="container">
      <aside className="sidebar">
        <div className="logo">HelpDesk</div>
        <ul className="nav">
          <li><span className="icon">🎫</span> <span className="label">Tickets</span></li>
          <li><span className="icon">👥</span> <span className="label">Agents</span></li>
          <li><span className="icon">⚙️</span> <span className="label">Settings</span></li>
        </ul>
        <div className="profile">
          <img src="https://i.pravatar.cc/40" alt="user" />
          <span>User</span>
        </div>
      </aside>

      <main className="ticket-detail-area">
        <h2>Ticket Detayları</h2>
        <p><strong>Açıklama:</strong> {ticket.description}</p>
        <p><strong>Kategori:</strong> {ticket.category}</p>
        <p><strong>Öncelik:</strong> {ticket.priority}</p>
        <p><strong>Oluşturulma Tarihi:</strong> {ticket.createdAt ? new Date(ticket.createdAt).toLocaleString() : "Tarih yok"}</p>
        <p><strong>Durum:</strong> {ticket.status}</p>

        <h3>Yanıtlar</h3>
        {responses.length === 0 ? (
          <p>Henüz yanıt yok.</p>
        ) : (
          <ul className="response-list">
            {responses.map((res, index) => (
              <li key={index}>
                <strong>Kullanıcı {res.userId}:</strong> {res.response}
                <br />
                <small>{new Date(res.dateTime).toLocaleString()}</small>
              </li>
            ))}
          </ul>
        )}

        {(isSupportUser || (!isSupportUser && ticket.status === 'Açık')) && (
          <div className="response-form">
            <h3>Yanıt Ver</h3>
            <textarea
              rows="4"
              value={responseText}
              onChange={(e) => setResponseText(e.target.value)}
              placeholder="Yanıtınızı yazınız..."
            />
            <button onClick={handleSubmitResponse}>Yanıt Gönder</button>
          </div>
        )}
      </main>
    </div>
  );
};

export default TicketDetailView;
