import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";
import ResponseBox from "../Components/ResponseBox";

const TicketDetailPage = () => {
  const { ticketId } = useParams();
  const [ticket, setTicket] = useState(null);

  useEffect(() => {
    const fetchTicketDetail = async () => {
      try {
        const token = localStorage.getItem("token");
        const res = await axios.get(`http://localhost:5094/api/ticket/${ticketId}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setTicket(res.data);
      } catch (err) {
        console.error("Ticket detay alınamadı", err);
      }
    };

    fetchTicketDetail();
  }, [ticketId]);

  if (!ticket) return <div>Yükleniyor...</div>;

  return (
    <div className="container flex min-h-screen">
      {/* Sidebar */}
      <div className="sidebar bg-gray-800 text-white w-64 p-4 flex flex-col justify-between">
        <div>
          <div className="logo text-2xl font-bold mb-6">HelpDesk</div>
          <ul className="nav space-y-4">
            <li className="flex items-center space-x-2">
              <span className="icon">🎫</span>
              <span className="label">Tickets</span>
            </li>
            <li className="flex items-center space-x-2">
              <span className="icon">👥</span>
              <span className="label">Agents</span>
            </li>
            <li className="flex items-center space-x-2">
              <span className="icon">⚙️</span>
              <span className="label">Settings</span>
            </li>
          </ul>
        </div>
        <div className="profile flex items-center space-x-2 mt-8">
          <img src="https://i.pravatar.cc/40" alt="user" className="rounded-full" />
          <span>User</span>
        </div>
      </div>

      {/* Main Content */}
      <div className="main-grid flex-1 p-6 bg-gray-100">
        <div className="form-box bg-white p-6 rounded-lg shadow-md">
          <h2 className="text-2xl font-bold mb-2">Ticket Başlığı: {ticket.title}</h2>
          <p className="mb-4">{ticket.description}</p>

          <h3 className="text-lg font-semibold mb-2">Mesajlar</h3>
          <ResponseBox ticketId={ticketId} />
        </div>
      </div>
    </div>
  );
};

export default TicketDetailPage;
