import React, { useEffect, useState, useCallback } from "react";
import { useNavigate } from "react-router-dom"; 
import { AgGridReact } from "ag-grid-react";
import "ag-grid-community/styles/ag-grid.css";
import "ag-grid-community/styles/ag-theme-alpine.css";
import { ClientSideRowModelModule } from 'ag-grid-community';
import { ModuleRegistry } from '@ag-grid-community/core';

ModuleRegistry.registerModules([ClientSideRowModelModule]);

const ListTickets = () => {
  const [rowData, setRowData] = useState([]);
  const navigate = useNavigate(); 
  const token = localStorage.getItem("token");

  const fetchTickets = useCallback(() => {
    fetch("http://localhost:5094/api/Ticket/getAllTicketsByRole", {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    })
      .then(res => {
        if (!res.ok) {
          if (res.status === 403) {
            throw new Error("Yetkisiz erişim");
          } else {
            throw new Error("Sunucu hatası");
          }
        }
        return res.json();
      })
      .then(data => {
        console.log("Gelen ticket verisi:", data);
        setRowData(data);
      })
      .catch(err => {
        console.error("Ticket verisi çekilirken hata oluştu:", err);
        alert("Ticket verisi alınamadı veya yetkiniz yok.");
        navigate("/login"); // Hatalıysa login sayfasına yönlendirilir
      });
  }, [token, navigate]);

  useEffect(() => {
    if (!token) {
      navigate("/login");
    } else {
      fetchTickets();
    }
  }, [token, fetchTickets, navigate]);

  const columnDefs = [
    { headerName: "ID", field: "id" },
    { headerName: "Kullanıcı ID", field: "userId" },
    { headerName: "Başlık", field: "title" },
    { headerName: "Açıklama", field: "description" },
    { headerName: "Durum", field: "status" },
    { headerName: "Kategori", field: "category" },
    { headerName: "Öncelik", field: "priority" },
    { 
      headerName: "Oluşturulma Tarihi", 
      field: "createdAt", 
      valueFormatter: params => {
        const date = new Date(params.value);
        return date.toLocaleString("tr-TR");
      }
    },
  ];

  return (
    <div className="container">
      <div className="helpdesk-container">
        {/* Buraya helpdesk kısmını ekleyebilirsiniz */}
        <div className="logo">Help Desk</div>
        <ul className="nav">
          <li>Ticket 1</li>
          <li>Ticket 2</li>
          <li>Ticket 3</li>
        </ul>
      </div>

      <div className="main-content">
        <div className="ticket-container">
          <div className="ticket-grid">
            <h3>Mevcut Talepler</h3>
            <div className="ag-theme-alpine">
              <AgGridReact
                rowData={rowData}
                columnDefs={columnDefs}
                modules={[ClientSideRowModelModule]}
                pagination={true}
                paginationPageSize={20}
                domLayout="autoHeight"
              />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ListTickets;
